    .accordion-control-section,
    #source_link {
        margin: 0;
    }
}

#athletics li,
#racing_games li,
#water_games li,
#indoor_games li {
    line-height: 36px;
    list-style-type: none;
    text-indent: 16px;
}

/* custom code end*/

@-moz-document url-prefix() {
    .e-accordion .e-content table {
      border-collapse: initial;
  }
  }

.e-accordion table {
    border-collapse: collapse;
