import { createRoot } from 'react-dom/client';
import './index.css';
import * as React from 'react';
import { useEffect } from 'react';

import { AccordionComponent, AccordionItemDirective, AccordionItemsDirective } from '@syncfusion/ej2-react-navigations';

const Accordion = () => {
    const accordionData = [
        { name: 'Robert', badge: '7 New' },
        { name: 'Kevin', badge: '27 New' },
        { name: 'Eric', badge: '2 New' },
        { name: 'Peter', badge: '14 New' }
    ];
    const accordionTemplate = () => {
        return (<div>
                <ul>
                    <li className='msg'>
                        <span className='e-acrdn-icons e-content-icon people'></span>
                        Message Thread
                    </li>
                    <li className='msg'>
                        <span className='e-acrdn-icons e-content-icon people'></span>
                        Message Thread
                    </li>
                </ul>
            </div>);
    };
    return (<div className='control-pane'>
            <div className='control-section badge-samples'>
                <div className="sample_container badge-accordion">
                    <AccordionComponent id="accordion">
                        <AccordionItemsDirective>
                                {accordionData.map((item, index) => (<AccordionItemDirective key={index} header={() => (<div>
                                                <span>{item.name}</span>
                                                <span className="e-badge e-badge-success">{item.badge}</span>
                                            </div>)} iconCss='e-people e-acrdn-icons' expanded={index === 0} content={accordionTemplate}/>))}
                        </AccordionItemsDirective>
                    </AccordionComponent>
                </div>
            </div>

        </div>);
};
export default Accordion;

const root = createRoot(document.getElementById('sample'));
root.render(<Accordion />);
