index.css:
.fabric .sample_container.badge-types .e-badge-showcase.e-card {
    background: transparent;
}

.control-section.badge-types-section {
    overflow: auto;
    padding-top: 40px;
}

.badge-types .e-btn {
    position: relative;
}

/* Media queries for Multiple devices */

.e-bigger .sample_container.badge-types .e-btn {
    padding: 0px 10px 2px 10px;
}

.highcontrast .sample_container.badge-types .e-btn:focus {
    outline: 0px;
    color: white;
}

.badge-types code {
    text-transform: none;
}

@media only screen and (max-width: 890px) {
    .sample_container.badge-types {
        max-width: 311px;
        margin: auto;
        margin-top: 0px;
    }
    .e-card-content.text {
        font-size: 10px;
    }
    .e-badge-showcase.e-card {
        width: 135px;
    }
}

@media only screen and (min-width: 890px) {
    .sample_container.badge-types {
        max-width: 745px;
        margin: auto;
        margin-top: 24px;
    }
    .e-badge-showcase.e-card {
        width: 160px;
    }
}

@media only screen and (max-width: 365px) {
    .sample_container.badge-types {
        max-width: 146px;
    }
}

.badge-types .e-btn {
    text-transform: none;
}

.sample_container.badge-types .badge-block {
    margin: 2px 2px 2px 2px;
    vertical-align: top;
    display: inline-block;
}

.highcontrast .sample_container.badge-types code {
    border: 0;
}

.sample_container.badge-types .badge-block-first {
    position: relative;
}

body.material-dark .e-badge-showcase.e-card,
body.tailwind-dark .e-badge-showcase.e-card,
body.bootstrap-dark .e-badge-showcase.e-card,
body.fabric-dark .e-badge-showcase.e-card {
    background-color: inherit;
}

/* Card Customization */

.e-badge-showcase.e-card {
    height: 100px;
    padding: 5px;
    margin: 5px 3px;
    box-shadow: none;
    border-radius: 8px;
    border: 1px solid #f1f1f1;
    background-color: #f1f1f1;
}

.highcontrast .e-badge-showcase.e-card,
.tailwind3-dark .e-badge-showcase.e-card,
.e-bigger.tailwind3-dark .e-badge-showcase.e-card {
    background-color: #000;
}

.e-bigger.tailwind3-dark .e-badge-showcase.e-card {
    width: 165px;
}

.e-badge-showcase.e-card .e-card-header .e-card-header-title {
    align-self: center;
    font-size: 18px;
    letter-spacing: 1px;
    text-shadow: #eaeaea 1px 1px 2px;
}

.e-badge-showcase.e-card .e-card-header .e-card-sub-title {
    color: rgba(0, 0, 0, 0.75);
    white-space: pre-line;
    font-size: 14px;
    text-shadow: #eaeaea 1px 1px 2px
}

.e-badge-showcase.e-card .e-card-header .e-card-sub-title p {
    margin: 0;
}

.e-badge-showcase.e-card .e-card-content {
    overflow: visible;
    align-self: center;
    padding: 0 0 10px 0;
    overflow: initial;
}

