index.css:
.breadcrumb-control-wrapper {
  width: 90%;
  margin: 0 auto;
  min-width: 85px;
}

.breadcrumb-control-wrapper div.row {
    padding: 15px 0px !important;
    margin: 0 !important;
}

@media only screen and (max-width: 480px) {
  .breadcrumb-control-wrapper {
      width: 92%;
  }

  .col-xs-12,
  .col-xs-4,
  .col-xs-12 {
      padding: 10px 5px;
      width: 100%;
  }

  .breadcrumb-control-wrapper div.row {
      padding: 0px !important;
  }
}

.tailwind .e-breadcrumb,
.tailwind-dark .e-breadcrumb {
margin-left: -12px;
}

.material .e-breadcrumb,
.material-dark .e-breadcrumb {
margin-left: -8px;
}

.fabric .e-breadcrumb,
.fabric-dark .e-breadcrumb,
.highcontrast .e-breadcrumb {
margin-left: -4px;
}

.reset-btn {
  float: right;
  margin: 5px 2px 5px 0;
}

index.js:
import { createRoot } from 'react-dom/client';
import './index.css';
import * as React from 'react';
import { BreadcrumbComponent } from '@syncfusion/ej2-react-navigations';
import { ButtonComponent } from '@syncfusion/ej2-react-buttons';
import { SampleBase } from './sample-base';

export class KeyboardNavigation extends SampleBase {
    breadcrumb;
    btnClick() {
        this.breadcrumb.activeItem = this.breadcrumb.items[this.breadcrumb.items.length - 1].text;
    }
    render() {
        return (<div className='control-pane'>
        <div className="control-section">
          <div className="content-wrapper breadcrumb-control-wrapper">
            <div className="row material2">
              <div className="col-xs-12 col-sm-12 col-lg-12 col-md-12">
                <h5 style={{ display: "inline-block" }}>Simple Breadcrumb</h5>
                <ButtonComponent cssClass='e-small reset-btn' onClick={this.btnClick.bind(this)}>Reset State</ButtonComponent>
              </div>
            </div>
            <div className="row material2">
              <div className="col-xs-12 col-sm-12 col-lg-12 col-md-12">
                <BreadcrumbComponent ref={(breadcrumbObj) => { this.breadcrumb = breadcrumbObj; }} enableNavigation={false}></BreadcrumbComponent>
              </div>
            </div>
          </div>
        </div>
      </div>);
    }
}

const root = createRoot(document.getElementById('sample'));
root.render(<KeyboardNavigation />);

