import { createRoot } from 'react-dom/client';
import './index.css';
/**
 * Right to left for bullet chart
 */
import * as React from 'react';
import { BulletChartComponent, BulletRangeCollectionDirective, BulletRangeDirective, BulletTooltip, Inject } from '@syncfusion/ej2-react-charts';
import { Browser } from '@syncfusion/ej2-base';
import { SampleBase } from './sample-base';
import { SliderComponent, ColorPickerComponent } from '@syncfusion/ej2-react-inputs';
import { DropDownListComponent } from '@syncfusion/ej2-react-dropdowns';
const SAMPLE_CSS = `
    .control-fluid {
		padding: 0px !important;
    }`;
export class BulletChartBarCustomization extends SampleBase {
    bulletChartInstance;
    bulletLoad = (args) => {
        let chart = document.getElementById('bar-customization');
        chart.setAttribute('title', '');
        let selectedTheme = location.hash.split('/')[1];
        selectedTheme = selectedTheme ? selectedTheme : 'Fluent2';
        args.bulletChart.theme = (selectedTheme.charAt(0).toUpperCase() + selectedTheme.slice(1)).replace(/-dark/i, 'Dark').replace(/light/i, 'Light').replace(/contrast/i, 'Contrast').replace(/-highContrast/i, 'HighContrast');
    };
    render() {
        return (<div className='control-pane'>
                <style>                 
                    {SAMPLE_CSS}                   
                </style>
                <div className='control-section col-md-8'>
                    <BulletChartComponent id='bar-customization' ref={chart => this.bulletChartInstance = chart} width={'100%'} animation={{ enable: false }} tooltip={{ enable: true }} valueField='value' targetField='target' minimum={0} maximum={300} interval={50} title='New Customers' titlePosition={Browser.isDevice ? 'Top' : 'Left'} subtitle='in Thousands' load={this.bulletLoad.bind(this)} dataSource={[{ value: 270, target: 250 }]}>
                        <Inject services={[BulletTooltip]}/>
                        <BulletRangeCollectionDirective>
                            <BulletRangeDirective end={150}></BulletRangeDirective>
                            <BulletRangeDirective end={250}></BulletRangeDirective>
                            <BulletRangeDirective end={300}></BulletRangeDirective>
                        </BulletRangeCollectionDirective>
                    </BulletChartComponent>
                </div>
                <div className='property-section col-md-4'>
                <div className="property-panel-header">Properties</div>
                    <table>
                    <tbody>
                    <tr style={{ height: '50px' }}>
                            <td style={{ width: '50%' }}>
                                <div className='prop-text'>Actual Value:</div>
                            </td>
                            <td style={{ width: '50%' }}>
                                <SliderComponent id='actualValue' min={0} max={300} value={270} tooltip={{ isVisible: true }} change={(args) => {
                this.bulletChartInstance.dataSource[0].value = args.value;
                this.bulletChartInstance.refresh();
            }}></SliderComponent>
                            </td>
                        </tr>
                        <tr style={{ height: '50px' }}>
                            <td style={{ width: '50%' }}>
                                <div className='prop-text'>Target Value:</div>
                            </td>
                            <td style={{ width: '50%' }}>
                                <SliderComponent min={0} max={300} value={250} tooltip={{ isVisible: true }} change={(args) => {
                this.bulletChartInstance.dataSource[0].target = args.value;
                this.bulletChartInstance.refresh();
            }} id='targetValue'></SliderComponent>
                            </td>
                        </tr>
                        <tr style={{ height: '50px' }}>
                            <td style={{ width: '50%' }}>
                                <div className='prop-text'>Feature Mode</div>
                            </td>
                            <td style={{ width: '50%' }}>
                                <DropDownListComponent id='featureType' value='Rect' dataSource={['Rect', 'Dot']} change={(args) => {
                this.bulletChartInstance.type = args.value;
                this.bulletChartInstance.refresh();
            }}></DropDownListComponent>


                            </td>
                        </tr>
                        <tr style={{ height: '50px' }}>
                            <td style={{ width: '50%' }}>
                                <div className='prop-text'>Value Color:</div>
                            </td>
                            <td style={{ width: '50%', textAlign: 'center' }}>
                                <ColorPickerComponent value='#000000' mode='Palette' change={(args) => {
                this.bulletChartInstance.valueFill = args.currentValue.hex;
                this.bulletChartInstance.refresh();
            }}></ColorPickerComponent>
                            </td>
                        </tr>
                        <tr style={{ height: '50px' }}>
                            <td style={{ width: '50%' }}>
                                <div className='prop-text'>Target Color:</div>
                            </td>
                            <td style={{ width: '50%', textAlign: 'center' }}>
                                <ColorPickerComponent value='#000000' mode='Palette' change={(args) => {
                this.bulletChartInstance.targetColor = args.currentValue.hex;
                this.bulletChartInstance.refresh();
            }}></ColorPickerComponent>
                            </td>
                        </tr>
                        </tbody>
                    </table>
                </div>
            </div>);
    }
}

const root = createRoot(document.getElementById('sample'));
root.render(<BulletChartBarCustomization />);

