import { createRoot } from 'react-dom/client';
import './index.css';
/**
 * Right to left for bullet chart
 */
import * as React from 'react';
import { BulletChartComponent, BulletRangeCollectionDirective, BulletRangeDirective, BulletTooltip, Inject } from '@syncfusion/ej2-react-charts';

import { DropDownListComponent } from '@syncfusion/ej2-react-dropdowns';
import { loadBulletChartTheme } from './theme-color';
const SAMPLE_CSS = `
      .control-fluid {
          padding: 0px !important;
      }
      .charts {
        align :center
    }`;
function BulletChartOrientation() {
    let bulletChartInstance;
    return (<div className='control-pane'>
            <style>
                {SAMPLE_CSS}
            </style>
            <div className='control-section col-md-8' style={{ textAlign: "center" }}>
                <BulletChartComponent id='bar-Orientation' ref={chart => bulletChartInstance = chart} width={'19%'} height={'400'} animation={{ enable: false }} tooltip={{ enable: true }} valueField='value' targetField='target' categoryField='name' minimum={0} maximum={30} interval={5} labelFormat='{value}%' margin={{ left: 10 }} titlePosition={'Top'} orientation='Vertical' load={bulletLoad.bind(this)} dataSource={[{ value: 23, target: 27, name: 'Product A' }]}>
                    <Inject services={[BulletTooltip]}/>
                    <BulletRangeCollectionDirective>
                        <BulletRangeDirective end={20}></BulletRangeDirective>
                        <BulletRangeDirective end={25}></BulletRangeDirective>
                        <BulletRangeDirective end={30}></BulletRangeDirective>
                    </BulletRangeCollectionDirective>
                </BulletChartComponent>
            </div>
            <div className='property-section col-md-4'>
                <div className="property-panel-header">Properties</div>
                <table>
                <tbody>
                    <tr>
                        <td style={{ width: '60%' }}>
                            <div className='prop-text'>Feature Mode</div>
                        </td>
                        <td style={{ width: '40%' }}>
                            <DropDownListComponent id='featureType' value='Vertical' dataSource={['Vertical', 'Horizontal']} change={(args) => {
            if (args.value === 'Horizontal') {
                bulletChartInstance.width = '80%';
                bulletChartInstance.height = '100px';
            }
            else {
                bulletChartInstance.width = '19%';
                bulletChartInstance.height = '400px';
            }
            bulletChartInstance.orientation = args.value;
            bulletChartInstance.refresh();
        }}></DropDownListComponent>
                        </td>
                    </tr>
                    </tbody>
                </table>
            </div>
        </div>);
    function bulletLoad(args) {
        let chart = document.getElementById('bar-Orientation');
        chart.setAttribute('title', '');
        loadBulletChartTheme(args);
    }
}
export default BulletChartOrientation;

const root = createRoot(document.getElementById('sample'));
root.render(<BulletChartOrientation />);
