/* custom code start */
#button-control {
    width: 100%;
    margin: 8% 0;
    text-align: center;
}

@media only screen and (max-width: 500px) {
    #button-control {
        margin-left: -3%;
    }
}

.button-section {
    width: 80%;
    margin: auto;
}

#button-control .col-xs-12 {
    margin: 15px 0;
}

/* custom code end */
/* ProgressButton sample icons */
@font-face {
    font-family: 'button-icons';
    src: url(data:application/x-font-ttf;charset=utf-8;base64,) format('truetype');
    font-weight: normal;
    font-style: normal;
}

.e-btn-sb-icons {
    font-family: 'button-icons';
    line-height: 1;
    font-style: normal;
    font-weight: normal;
    font-variant: normal;
    text-transform: none;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
}

.e-play-icon::before {
    content: '\e701';
}

.e-pause-icon::before {
    content: '\e705';
}

.e-open-icon::before {
    content: '\e70d';
}

.e-add-icon::before {
    content: '\e70a';
}
