index.css:
/* custom code start */
#progress-button-control {
    width: 100%;
    margin: 6% 0;
    text-align: center;
}

@media only screen and (max-width: 500px) {
    #progress-button-control {
        margin-left: -11%;
    }
}

.progress-button-section {
    width: 80%;
    margin: auto;
}

#progress-button-control .col-xs-12 {
    margin: 18px 0;
}

#contract.e-progress-active .e-btn-content {
    display: none;
}

.material3 #roundbtn .e-play-icon,
.material3-dark #roundbtn .e-play-icon {
    margin-top: -4px;
    margin-left: 2px;
 }

 .e-bigger .material3 #roundbtn .e-play-icon,
 .e-bigger .material3-dark #roundbtn .e-play-icon {
    margin-top: -4px;
    margin-left: 2px;
 }
/* custom code end */
#contract {
    transition: all .3s cubic-bezier(.175, .885, .32, 1) 100ms;
}

.fabric #roundbtn .e-play-icon,
.bootstrap #roundbtn .e-play-icon,
.highcontrast #roundbtn .e-play-icon {
    margin-top: -2px;
}

/* ProgressButton sample icons */
@font-face {
    font-family: 'button-icons';
    src:url(data:application/x-font-ttf;charset=utf-8;base64,) format('truetype');
    font-weight: normal;
    font-style: normal;
}

.e-btn-sb-icons {
    font-family: 'button-icons';
    line-height: 1;
    font-style: normal;
    font-weight: normal;
    font-variant: normal;
    text-transform: none;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
}

.e-btn-sb-icons.e-play-icon::before {
    content: '\e701';
    font-size: 12px;
}

.e-bigger .e-btn-sb-icons.e-play-icon::before {
    font-size: 14px;
}

.e-btn-sb-icons.e-download-icon::before {
    content: '\e702';
}

