index.css:
.partial-carousel-section .carousel-sample {
    margin: 0 auto 2em;
    max-width: 650px;
    height: 300px;
}

.partial-carousel .e-carousel-items .e-carousel-item .img-container {
    height: 300px;
    width: 100%;
    margin: 0 10px;
}

.partial-carousel .e-carousel-items .e-carousel-item .img-caption {
    bottom: 4em;
    color: #fff;
    font-size: 12pt;
    height: 2em;
    position: relative;
    padding: 0.3em 1em;
    text-align: center;
    width: 100%;
}

@media screen and (max-width: 480px) {
    .partial-carousel-section .carousel-sample {
        height: 110px;
    }

    .partial-carousel .e-carousel-items .e-carousel-item .img-caption {
        position: absolute;
        top: 0;
        font-size: 12px;
        text-align: left;
    }
}

index.js:
import { createRoot } from 'react-dom/client';
import './index.css';
import * as React from 'react';
import { CarouselComponent, CarouselItemsDirective, CarouselItemDirective } from '@syncfusion/ej2-react-navigations';
import { SampleBase } from './sample-base';

export class PartialVisible extends SampleBase {
    itemTemplate1() {
        return (<figure className="img-container">
      <img src="https://ej2.syncfusion.com/react/demos/src/carousel/images/bridge.jpg" alt="bridge" style={{ height: "100%", width: "100% " }}/>
      <figcaption className="img-caption">Golden Gate Bridge, San Francisco</figcaption>
    </figure>);
    }
    itemTemplate2() {
        return (<figure className="img-container">
      <img src="https://ej2.syncfusion.com/react/demos/src/carousel/images/trees.jpg" alt="trees" style={{ height: "100%", width: "100% " }}/>
      <figcaption className="img-caption">Spring Flower Trees</figcaption>
    </figure>);
    }
    itemTemplate3() {
        return (<figure className="img-container">
      <img src="https://ej2.syncfusion.com/react/demos/src/carousel/images/waterfall.jpg" alt="waterfall" style={{ height: "100%", width: "100% " }}/>
      <figcaption className="img-caption">Oddadalen Waterfalls, Norway</figcaption>
    </figure>);
    }
    itemTemplate4() {
        return (<figure className="img-container">
      <img src="https://ej2.syncfusion.com/react/demos/src/carousel/images/sea.jpg" alt="sea" style={{ height: "100%", width: "100% " }}/>
      <figcaption className="img-caption">Anse Source d`Argent, Seychelles</figcaption>
    </figure>);
    }
    itemTemplate5() {
        return (<figure className="img-container">
      <img src="https://ej2.syncfusion.com/react/demos/src/carousel/images/rocks.jpeg" alt="rocks" style={{ height: "100%", width: "100% " }}/>
      <figcaption className="img-caption">Stonehenge, England</figcaption>
    </figure>);
    }
    render() {
        return (<div className='control-pane'>
        <div className='control-section partial-carousel-section'>
          <div className='control carousel-sample'>
            {/* Render the Carousel Component */}
            <CarouselComponent cssClass="partial-carousel" partialVisible={true}>
              <CarouselItemsDirective>
                <CarouselItemDirective template={this.itemTemplate1.bind(this)}/>
                <CarouselItemDirective template={this.itemTemplate2.bind(this)}/>
                <CarouselItemDirective template={this.itemTemplate3.bind(this)}/>
                <CarouselItemDirective template={this.itemTemplate4.bind(this)}/>
                <CarouselItemDirective template={this.itemTemplate5.bind(this)}/>
              </CarouselItemsDirective>
            </CarouselComponent>
          </div></div>
      </div>);
    }
}

const root = createRoot(document.getElementById('sample'));
root.render(<PartialVisible />);

