import { createRoot } from 'react-dom/client';
import './index.css';
import * as React from 'react';
import { SampleBase } from './sample-base';
import { AccumulationChartComponent, AccumulationSeriesCollectionDirective, AccumulationSeriesDirective, AccumulationLegend, PieSeries, AccumulationDataLabel, AccumulationTooltip, Inject } from '@syncfusion/ej2-react-charts';
import { Browser } from '@syncfusion/ej2/base';
export let data1 = [
    { x: 'Argentina', y: 505370, r: Browser.isDevice ? '110' : '100', text: 'Argentina' },
    { x: 'Belgium', y: 551500, r: Browser.isDevice ? '120' : '118.7', text: 'Belgium' },
    { x: 'Dominican Republic', y: 312685, r: '137.5', text: Browser.isDevice ? 'Dominican <br> Republic' : 'Dominican Republic' },
    { x: 'Cuba', y: 350000, r: '124.6', text: 'Cuba' },
    { x: 'Egypt', y: 301000, r: '150.8', text: 'Egypt' },
    { x: 'Kazakhstan', y: 300000, r: '155.5', text: 'Kazakhstan' },
    { x: 'Somalia', y: 357022, r: '160.6', text: 'Somalia' }
];
export class PieRadius extends SampleBase {
    pie;
    render() {
        return (<div className='control-pane'>
      <div className='control-section'>
          <AccumulationChartComponent id='pie-chart' legendSettings={{ visible: true, reverse: true }} enableSmartLabels={true} title='Pie with different Radius' enableBorderOnMouseMove={false} enableAnimation={true} load={this.load.bind(this)} loaded={this.loaded.bind(this)} tooltip={{ enable: true, format: '<b>${point.x}</b><br/>Area in square km: <b>${point.y} </b> <br/> Population density per square km: <b>${point.tooltip}</b>', enableHighlight: true }}>
              <Inject services={[AccumulationLegend, PieSeries, AccumulationDataLabel, AccumulationTooltip]}/>
              <AccumulationSeriesCollectionDirective>
                  <AccumulationSeriesDirective dataSource={data1} xName='x' yName='y' innerRadius='20%' tooltipMappingName='r' dataLabel={{ visible: true, position: Browser.isDevice ? 'Inside' : 'Outside', name: 'text', enableRotation: true, font: { fontWeight: '600' }, connectorStyle: { length: '20px', type: 'Curve' } }} radius='r'/>
              </AccumulationSeriesCollectionDirective>
          </AccumulationChartComponent>
      </div>
  </div>);
    }
    load(args) {
        let selectedTheme = location.hash.split('/')[1];
        selectedTheme = selectedTheme ? selectedTheme : 'Fluent2';
        args.accumulation.theme = (selectedTheme.charAt(0).toUpperCase() + selectedTheme.slice(1)).
            replace(/-dark/i, "Dark").replace(/contrast/i, 'Contrast');
    }
    ;
    loaded(args) {
        let chart = document.getElementById('pie-chart');
        chart.setAttribute('title', '');
    }
    ;
}

const root = createRoot(document.getElementById('sample'));
root.render(<PieRadius />);

