.api-chatui {
    height: 500px;
    width: 60%;
    margin: 0 auto;
}

.api-chatui .chat_header_icon {
    background-image: url('https://ej2.syncfusion.com/react/demos/src/chat-ui/images/public-event.png');
    background-color: unset;
}

.chat-property-section .property-panel-content td {
    padding: 10px 0px;
}

.chat-property-section .e-multiselect,
.chat-property-section .e-multi-select-wrapper {
    padding-left: 0;
    padding-top: 0;
}

@media only screen and (max-width: 850px) {
    .api-chatui {
        width: 80%;
    }
}

property-pane.js:
import * as ReactDOM from 'react-dom';
import * as React from 'react';
import { select } from '@syncfusion/ej2-base';
export class PropertyPane extends React.Component {
    render() {
        const mobilePropPane = select('.sb-mobile-prop-pane');
        const isMobile = window.matchMedia('(max-width:550px)').matches;
        return isMobile && mobilePropPane ?
            ReactDOM.createPortal(<div className='property-panel-section'>
                    <div className="property-panel-header">
                        {this.props.title}
                    </div>
                    <div className="property-panel-content">
                        {this.props.children}
                    </div>
                </div>, mobilePropPane)
            :
                (<div className='property-panel-section'>
                    <div className="property-panel-header">
                        {this.props.title}
                    </div>
                    <div className="property-panel-content">
                        {this.props.children}
                    </div>
                </div>);
    }
}

