index.js:
import { createRoot } from 'react-dom/client';
import './index.css';
import * as React from 'react';
import { ChatUIComponent } from '@syncfusion/ej2-react-interactive-chat';
import { SwitchComponent } from '@syncfusion/ej2-react-buttons';
import { DropDownListComponent, MultiSelectComponent } from '@syncfusion/ej2-react-dropdowns';
import { SampleBase } from './sample-base';

import * as data from './messageData.json';
import { PropertyPane } from './property-pane';
export class API extends SampleBase {
    chatUiInst;
    handleSwitchChange = (property, checked) => {
        this.chatUiInst[property] = checked;
    };
    handleDropDownChange = (property, value) => {
        this.chatUiInst[property] = value;
    };
    handleMultiSelectChange = (args, action) => {
        const user = { user: args.itemData, avatarBgColor: '#87cefa' };
        if (['Laura', 'Charlie'].includes(args.itemData)) {
            user.avatarBgColor = args.itemData === 'Charlie' ? '#e6cdde' : '#dec287';
            user.avatarUrl = `./src/chat-ui/images/${args.itemData.toLowerCase()}.png`;
        }
        if (action === 'select') {
            this.chatUiInst.typingUsers = [...this.chatUiInst.typingUsers, user];
        }
        else {
            this.chatUiInst.typingUsers = this.chatUiInst.typingUsers.filter(user => user.user !== args.itemData);
        }
    };
    render() {
        // Parse the date strings in the JSON data to Date objects
        const messages = data["communityMessagedata"].map(message => ({
            ...message,
            timeStamp: (message.timeStamp ? new Date(message.timeStamp) : new Date())
        }));
        return (<div className='control-pane'>
                <div className="col-lg-8 control-section">
                    <div className="api-chatui">
                        <ChatUIComponent messages={messages} user={{ user: 'Alice', id: 'admin' }} headerIconCss="chat_header_icon" headerText="Design Community" showTimeBreak={true} timeStampFormat="MM/dd hh:mm a"/>
                    </div>
                </div>
                <div className="col-lg-4 property-section chat-property-section">
                    <PropertyPane title='Properties'>
                        <table id="property" title="Properties">
                            <tbody>
                                <tr>
                                    <td><div>Timestamp format</div></td>
                                    <td style={{ paddingRight: "10px" }}>
                                        <DropDownListComponent id="chat_dateformats" dataSource={['MM/dd hh:mm a', 'dd/MM/yy hh:mm a', 'hh:mm a', 'MMMM hh:mm a']} placeholder="Format" width="180px" change={(e) => this.handleDropDownChange('timeStampFormat', e.itemData.value)}/>
                                    </td>
                                </tr>
                                <tr>
                                    <td><div>Show timestamp</div></td>
                                    <td style={{ paddingRight: "10px" }}>
                                        <SwitchComponent id="chatTimestamp" checked={true} change={(e) => this.handleSwitchChange('showTimeStamp', e.checked)}/>
                                    </td>
                                </tr>
                                <tr>
                                    <td><div>Show timebreak</div></td>
                                    <td style={{ paddingRight: "10px" }}>
                                        <SwitchComponent id="chatTimebreak" checked={true} change={(e) => this.handleSwitchChange('showTimeBreak', e.checked)}/>
                                    </td>
                                </tr>
                                <tr>
                                    <td><div>Show header</div></td>
                                    <td style={{ paddingRight: "10px" }}>
                                        <SwitchComponent id="chatHeader" checked={true} change={(e) => this.handleSwitchChange('showHeader', e.checked)}/>
                                    </td>
                                </tr>
                                <tr>
                                    <td><div>Show footer</div></td>
                                    <td style={{ paddingRight: "10px" }}>
                                        <SwitchComponent id="chatFooter" checked={true} change={(e) => this.handleSwitchChange('showFooter', e.checked)}/>
                                    </td>
                                </tr>
                                <tr>
                                    <td><div>Typing users</div></td>
                                    <td style={{ paddingRight: "10px" }}>
                                        <MultiSelectComponent id="chat_typingUsers" dataSource={['Michale', 'Laura', 'Charlie']} placeholder="Typing users..." select={(e) => this.handleMultiSelectChange(e, 'select')} removed={(e) => this.handleMultiSelectChange(e, 'removed')}/>
                                    </td>
                                </tr>
                            </tbody>
                        </table>
                    </PropertyPane>
                </div>
            </div>);
    }
}

const root = createRoot(document.getElementById('sample'));
root.render(<API />);

