index.css:
#chip-default-wrapper .sample-padding {
    padding: 10px;
    display: flex;
    justify-content: center;
    align-items: center;
}

.chips-headers {
    font-size: 12px;
    text-align: center;
    padding-top: 10px;
}

#chip-choice .e-chip .e-chip-icon {
    font-family: 'e-chips-icons';
}

#chip-choice .text::before {
    content: '\e701';
}

#chip-choice .mail::before {
    content: '\e700';
}

#chip-choice .alarm::before {
    content: '\e704';
}

#chip-choice .game::before {
    content: '\e703';
}

@font-face {
    font-family: 'e-chips-icons';
    src:
        url(data:application/x-font-ttf;charset=utf-8;base64,) format('truetype');
    font-weight: normal;
    font-style: normal;
}

#chip-default-wrapper .anne {
    background-image: url('https://ej2.syncfusion.com/react/demos/src/chips/images/anne.png');
}

#chip-default-wrapper .janet {
    background-image: url('https://ej2.syncfusion.com/react/demos/src/chips/images/janet.png');
}

#chip-default-wrapper .laura {
    background-image: url('https://ej2.syncfusion.com/react/demos/src/chips/images/laura.png');
}

#chip-default-wrapper .margaret {
    background-image: url('https://ej2.syncfusion.com/react/demos/src/chips/images/margaret.png');
}

