import { createRoot } from 'react-dom/client';
import './index.css';
import * as React from 'react';
import { useEffect, useState } from 'react';

import { ComboBoxComponent } from '@syncfusion/ej2-react-dropdowns';
import { CheckBoxComponent } from '@syncfusion/ej2-react-buttons';
import { DataManager, Query, WebApiAdaptor } from '@syncfusion/ej2-data';
import { PropertyPane } from './property-pane';

import * as data from './dataSource.json';
const Data = () => {
    const temp = 'sportsData';
    // define the JSON of data
    const sportsData = data[temp];
    // bind the DataManager instance to dataSource property
    const customerData = new DataManager({
        url: 'http://localhost:62728/api/Employees',
        adaptor: new WebApiAdaptor,
        crossDomain: true
    });
    // bind the Query instance to query property
    const query = new Query().select(['FirstName', 'EmployeeID']).take(10).requiresCount();
    // maps the remote data column to fields property
    const remoteFields = { text: 'FirstName', value: 'EmployeeID' };
    // maps the local data column to fields property
    const localFields = { text: 'Game', value: 'Id' };
    // enable or disable the autofill in ComboBox based on CheckBox checked state
    const [autofill, setAutofill] = useState(true);
    // Bind change event
    const onChange = (args) => {
        setAutofill(args.checked);
    };
    return (<div className='control-pane'>
            <div className='control-section'>
                <div className='col-lg-9'>
                    <div className='col-lg-6'>
                        <div className='drop-down-list-content' id="local">
                        <label className="h4"> Local Data</label>
                            <ComboBoxComponent id="games" dataSource={sportsData} fields={localFields} placeholder="Select a game" popupHeight="220px" autofill={autofill}/>
                        </div>
                    </div>
                    <div className='col-lg-6'>
                        <div className='drop-down-list-content' id="remote">
                        <label className="h4">Remote Data</label>
                            <ComboBoxComponent id="customers" dataSource={customerData} sortOrder="Ascending" query={query} fields={remoteFields} placeholder="Select a name" autofill={autofill} popupHeight="220px"/>
                        </div>
                    </div>
                </div>
                <div className='col-lg-3 property-section'>
                    <PropertyPane title='Properties'>
                        <div style={{ marginLeft: '75px', paddingTop: '35px' }}>
                            <CheckBoxComponent checked={autofill} label='Autofill' change={onChange.bind(this)}/>
                        </div>
                    </PropertyPane>
                </div>
            </div>
        </div>);
};
export default Data;

const root = createRoot(document.getElementById('sample'));
root.render(<Data />);
