index.css:
/* custom code start */

/* template support css*/
.e-contextMenu-template .menu-wrapper{
    display: flex;
    align-items: center;
    padding: 2px;
}

.e-contextMenu-template .menu-wrapper .text-content {
    display: flex;
    flex-direction: column;
}

.e-contextMenu-template .menu-wrapper .text {
    font-weight: 600;
}

.e-contextMenu-template .menu-wrapper .description {
    font-size: 0.8em;
}

.e-contextMenu-template .menu-wrapper .icon-right {
    padding: 8px 8px 8px 0px;
    font-size: 1.5em;
}

.e-contextMenu-template .e-caret {
    margin-top: -34px !important;
}

.e-contextMenu-template .e-menu-item {
    height: auto !important;
    line-height: unset !important;
}

#contextmenu-control {
    margin: 5% 25%;
    width: auto;
    -webkit-touch-callout: none; /* iOS Safari */
    -webkit-user-select: none; /* Safari */
}

#contextmenutarget {
    border: 1px dashed;
    height: 250px;
    padding: 10px;
    position: relative;
    text-align: center;
    justify-content: center;
    font-size: 14px;
    display: flex;
    align-items: center;
}

@media only screen and (max-width: 700px) {
    #contextmenu-control {
        margin: 5% 10%;
        width: auto;
    }
    #contextmenutarget {
        font-size: 12px;
    }
}

.e-contextMenu-template .e-menu-item .e-previous {
    margin-right: 0 !important;
}

/* custom code end */

index.js:
import { createRoot } from 'react-dom/client';
import './index.css';
import * as React from 'react';
import { ContextMenuComponent } from '@syncfusion/ej2-react-navigations';
import { Browser } from '@syncfusion/ej2-base';
import { SampleBase } from './sample-base';

export class Template extends SampleBase {
    menuObj;
    content = Browser.isDevice ? 'Touch hold to open the ContextMenu' :
        'Right click/Touch hold to open the ContextMenu';
    template = "<div class='menu-wrapper'><span class='${iconCss} icon-right'></span><div class='text-content'><span class='text'>${answerType}</span><span class='description'>${description}</span></div></div>";
    //ContextMenu items definition
    menuItems = [
        {
            answerType: 'Selection',
            description: "Choose from options",
            iconCss: 'e-icons e-list-unordered'
        },
        {
            answerType: 'Yes / No',
            description: "Select Yes or No",
            iconCss: 'e-icons e-check-box',
        },
        {
            answerType: 'Text',
            description: "Type own answer",
            iconCss: 'e-icons e-caption',
            items: [
                {
                    answerType: 'Single line',
                    description: "Type answer in a single line",
                    iconCss: 'e-icons e-text-form'
                },
                {
                    answerType: 'Multiple line',
                    description: "Type answer in multiple line",
                    iconCss: 'e-icons e-text-wrap'
                }
            ]
        },
        {
            answerType: 'None',
            iconCss: 'e-icons e-comment-show',
            description: "No answer required"
        },
    ];
    addTemplateClass = (args) => {
        if (args.element.classList.contains('e-ul')) {
            args.element.classList.add('e-contextMenu-template');
        }
    };
    render() {
        return (<div className='control-pane'>
                <div className='control-section'>
                    <div className='contextmenu-section'>
                        <div id='contextmenu-control'>
                            <div id="contextmenutarget">{this.content}</div>
                            <ContextMenuComponent className="e-contextMenu-template" target='#contextmenutarget' items={this.menuItems} itemTemplate={this.template} beforeOpen={this.addTemplateClass}/>
                        </div>
                    </div>
                </div>
            </div>);
    }
}

const root = createRoot(document.getElementById('sample'));
root.render(<Template />);

