import { createRoot } from 'react-dom/client';
import './index.css';
import * as React from 'react';
import { useEffect, useState, useMemo } from 'react';

import { DateRangePickerComponent, PresetsDirective, PresetDirective } from '@syncfusion/ej2-react-calendars';

const getInitialLabels = (culture) => {
    const labels = {
        en: {
            thisWeek: "This Week",
            thisMonth: "This Month",
            lastMonth: "Last Month",
            lastYear: "Last Year",
        },
        de: {
            thisWeek: "Diese Woche",
            thisMonth: "Dieser Monat",
            lastMonth: "Letzter Monat",
            lastYear: "Letztes Jahr",
        },
        "fr-CH": {
            thisWeek: "Cette semaine",
            thisMonth: "Ce mois-ci",
            lastMonth: "Le mois dernier",
            lastYear: "L'année dernière",
        },
        ar: {
            thisWeek: "هذا الأسبوع",
            thisMonth: "هذا الشهر",
            lastMonth: "الشهر الماضي",
            lastYear: "السنة الماضية",
        },
        zh: {
            thisWeek: "本周",
            thisMonth: "本月",
            lastMonth: "上个月",
            lastYear: "去年",
        },
    };
    return labels[culture] || labels.en;
};
const Presets = () => {
    // Cast the element to the correct type
    const currentCultureElement = document.getElementById("sb-setting-culture_hidden");
    const currentCulture = currentCultureElement?.value || "en";
    const [labels] = useState(() => getInitialLabels(currentCulture));
    const dates = useMemo(() => {
        const weekStart = new Date(new Date(new Date().setDate(new Date().getDate() - (new Date().getDay() + 7) % 7)).toDateString());
        const weekEnd = new Date(new Date(new Date().setDate(new Date(new Date().setDate((new Date().getDate()
            - (new Date().getDay() + 7) % 7))).getDate() + 6)).toDateString());
        const monthStart = new Date(new Date(new Date().setDate(1)).toDateString());
        const monthEnd = new Date(new Date(new Date(new Date().setMonth(new Date().getMonth() + 1)).setDate(0)).toDateString());
        const lastStart = new Date(new Date(new Date(new Date().setMonth(new Date().getMonth() - 1)).setDate(1)).toDateString());
        const lastEnd = new Date(new Date(new Date().setDate(0)).toDateString());
        const yearStart = new Date(new Date(new Date().getFullYear() - 1, 0, 1).toDateString());
        const yearEnd = new Date(new Date(new Date().getFullYear() - 1, 11, 31).toDateString());
        return { weekStart, weekEnd, monthStart, monthEnd, lastStart, lastEnd, yearStart, yearEnd };
    }, [labels]);
    return (<div className='control-pane'>
            <div className='control-section'>
                <div className='datepicker-control-section'>
                    <DateRangePickerComponent placeholder='Select a range'>
                        <PresetsDirective>
                            <PresetDirective label={labels.thisWeek} start={dates.weekStart} end={dates.weekEnd}></PresetDirective>
                            <PresetDirective label={labels.thisMonth} start={dates.monthStart} end={dates.monthEnd}></PresetDirective>
                            <PresetDirective label={labels.lastMonth} start={dates.lastStart} end={dates.lastEnd}></PresetDirective>
                            <PresetDirective label={labels.lastYear} start={dates.yearStart} end={dates.yearEnd}></PresetDirective>
                        </PresetsDirective>
                    </DateRangePickerComponent>
                </div>
            </div>
        </div>);
};
export default Presets;

const root = createRoot(document.getElementById('sample'));
root.render(<Presets />);
