import { createRoot } from 'react-dom/client';
import './index.css';
import * as React from 'react';
import { useState, useEffect } from 'react';
import { DialogComponent } from '@syncfusion/ej2-react-popups';


const DefaultFunctionalities = () => {
    let buttons;
    const [display, setDisplay] = useState('none');
    const [status, setStatus] = useState(true);
    const animationSettings = { effect: 'None' };
    const dlgButtonClick = () => {
        window.open("https://www.syncfusion.com/company/about-us");
    };
    buttons = [
        {
            click: dlgButtonClick,
            buttonModel: {
                content: 'Learn More',
                isPrimary: true,
            }
        }
    ];
    const buttonClick = () => {
        setStatus(true);
    };
    const dialogClose = () => {
        setStatus(false);
        setDisplay('inline-block');
    };
    const dialogOpen = () => {
        setStatus(true);
        setDisplay('none');
    };
    return (<div className="control-pane">
      <div id="targetElement" className="control-section col-lg-12 defaultDialog dialog-target">
        <button className="e-control e-btn dlgbtn" style={{ display: display }} onClick={buttonClick} id="dialogBtn">Open</button>
        <DialogComponent id="defaultdialog" showCloseIcon={true} animationSettings={animationSettings} width="500px" target={'#targetElement'} header="About SYNCFUSION Succinctly Series" visible={status} buttons={buttons} open={dialogOpen} close={dialogClose}>
          <div>
            <div>
              In the Succinctly series, Syncfusion created a robust free library
              of more than 130 technical e-books formatted for PDF, Kindle, and
              EPUB.
              <br />
              <br />
              The Succinctly series was born in 2012 out of a desire to provide
              concise technical e-books for software developers Each title in
              the Succinctly series is written by a carefully chosen expert and
              provides essential content in about 100 pages.
            </div>
          </div>
        </DialogComponent>
      </div>
    </div>);
};
export default DefaultFunctionalities;

const root = createRoot(document.getElementById('sample'));
root.render(<DefaultFunctionalities />);
