import { createRoot } from 'react-dom/client';
import './index.css';
import * as React from 'react';
import { DialogComponent } from '@syncfusion/ej2-react-popups';
import { SampleBase } from './sample-base';

export class Resizable extends SampleBase {
    resizableDialogInstance;
    animationSettings;
    buttonEle;
    buttonRef;
    constructor(props) {
        super(props);
        this.state = {
            hideDialog: true
        };
        this.buttonRef = element => {
            this.buttonEle = element;
        };
        this.dialogClose = this.dialogClose.bind(this);
        this.dialogOpen = this.dialogOpen.bind(this);
        this.animationSettings = { effect: 'None' };
    }
    buttonClick(args) {
        this.setState({ hideDialog: true });
    }
    dialogClose() {
        this.setState({ hideDialog: false });
        this.buttonEle.style.display = 'inline-block';
    }
    dialogOpen() {
        this.setState({ hideDialog: true });
        this.buttonEle.style.display = 'none';
    }
    render() {
        return (<div className='control-pane'>
                <div id='target' className='col-lg-12 control-section dialog-resizable'>
                    <button className='e-control e-btn dlgbtn' ref={this.buttonRef} onClick={this.buttonClick.bind(this)} id='dialogBtn'>Open Dialog</button>
                    {/* Render resizable Dialog */}
                    <DialogComponent id='resizableDialog' header='Resize Me!!!' allowDragging={true} showCloseIcon={true} animationSettings={this.animationSettings} width='300px' ref={resizableDialog => this.resizableDialogInstance = resizableDialog} target='#target' visible={this.state.hideDialog} enableResize={true} resizeHandles={['All']} open={this.dialogOpen} close={this.dialogClose}>
                        This is a dialog with resizable support.
            </DialogComponent>
                </div>
            </div>);
    }
}

const root = createRoot(document.getElementById('sample'));
root.render(<Resizable />);

