import { createRoot } from 'react-dom/client';
import './index.css';
import * as React from 'react';
import { useEffect } from 'react';

import { DropDownListComponent } from '@syncfusion/ej2-react-dropdowns';

import * as data from './dataSource.json';
const Templates = () => {
    const temp = 'empList';
    // define the JSON of data
    const employeesData = data[temp];
    // maps the appropriate column to fields property
    const fields = { text: 'Name', value: 'Eimg' };
    //set the value to header template
    const headerTemplate = () => {
        return (<div className="header"> <span>Photo</span> <span className="columnHeader">Employee Info</span></div>);
    };
    //set the value to item template
    const itemTemplate = (data) => {
        return (<div><img className="empImage" src={"https://ej2.syncfusion.com/react/demos/src/drop-down-list/Employees/" + data['Eimg'] + ".png"} alt="employee"/>
                <div className="ename"> {data.Name} </div><div className="job"> {data.Designation} </div></div>);
    };
    //set the value to value template
    const valueTemplate = (data) => {
        return (<div className="valueTemplate"><img className="value" src={"https://ej2.syncfusion.com/react/demos/src/drop-down-list/Employees/" + data.Eimg + ".png"} height="28px" width="28px" alt="employee"/>
                <div className="name"> {data.Name} </div></div>);
    };
    return (<div className='control-pane'>
            <div className='control-section'>
                <div id='template'>
                    <DropDownListComponent id="employees" dataSource={employeesData} fields={fields} placeholder="Select an employee" itemTemplate={itemTemplate} valueTemplate={valueTemplate} headerTemplate={headerTemplate} popupHeight="270px"/>
                </div>
            </div>
        </div>);
};
export default Templates;

const root = createRoot(document.getElementById('sample'));
root.render(<Templates />);
