import { createRoot } from 'react-dom/client';
import './index.css';
import * as React from 'react';
import { SampleBase } from './sample-base';
import { DropDownTreeComponent } from '@syncfusion/ej2-react-dropdowns';
import { DataManager, Query, ODataV4Adaptor } from '@syncfusion/ej2-data';

export class RemoteData extends SampleBase {
    // Use data manager to get dropdown tree data from remote source
    data = new DataManager({
        url: 'https://services.odata.org/V4/Northwind/Northwind.svc',
        adaptor: new ODataV4Adaptor,
        crossDomain: true,
    });
    // Set queries to filter and fetch remote data
    query = new Query().from('Employees').select('EmployeeID,FirstName,Title').take(5);
    query1 = new Query().from('Orders').select('OrderID,EmployeeID,ShipName').take(5);
    fields = {
        dataSource: this.data, query: this.query, value: 'EmployeeID', text: 'FirstName', hasChildren: 'EmployeeID',
        child: { dataSource: this.data, query: this.query1, value: 'OrderID', parentValue: 'EmployeeID', text: 'ShipName' }
    };
    render() {
        return (<div className='control-pane'>
        <div className='control-section dropdowntree-remote'>
          <div id="remote">
            <DropDownTreeComponent id="ddtremote" fields={this.fields} placeholder="Select a name" popupHeight="200px"/>
          </div>
        </div>

      </div>);
    }
}

const root = createRoot(document.getElementById('sample'));
root.render(<RemoteData />);

