import { createRoot } from 'react-dom/client';
import './index.css';
import * as React from 'react';
import { useEffect } from 'react';
import { DropDownTreeComponent } from '@syncfusion/ej2-react-dropdowns';


import * as dataSource from './template-data.json';
const Templates = () => {
    const data = dataSource;
    // maps the appropriate column to fields property
    const fields = { dataSource: data.templateData, value: 'id', parentValue: 'pid', text: 'name', hasChildren: 'hasChild' };
    //set the value to header template
    const headerTemplate = () => {
        return (<div className="head"> Employee List </div>);
    };
    //set the value to item template
    const itemTemplate = (data) => {
        return (<div> <img className="eimage" src={"https://ej2.syncfusion.com/react/demos/src/images/employees/" + data['eimg'] + ".png"}/>
                <div> <div className="ename"> {data.name} </div><div className="ejob"> {data.job} </div><span className="e-badge icons"><span className={data['status']}></span></span></div></div>);
    };
    //set the value to footer template
    const footerTemplate = () => {
        return (<div className="footer"> <div className="footer-content">
                <span className="e-badge">
                    <span className="display available">
                        <span className="status online"></span>
                        Available
                    </span>
                    <span className="display meeting">
                        <span className="status busy"></span>
                        Busy
                    </span>
                    <span className="display unavailable">
                        <span className="status away"></span>
                        Away
                    </span>
                </span>
            </div> </div>);
    };
    //set the value to value template
    const valueTemplate = (data) => {
        return (<div>
                <div> {data.name} - {data.job} </div>
            </div>);
    };
    return (<div className='control-pane'>
            <div className='control-section dropdowntree-templates'>
                <div className='control_wrapper'>
                    <DropDownTreeComponent fields={fields} placeholder="Select an employee" itemTemplate={itemTemplate} footerTemplate={footerTemplate} headerTemplate={headerTemplate} valueTemplate={valueTemplate} popupHeight="270px" cssClass="ddt-template" width="100%"/>
                </div>
            </div>

        </div>);
};
export default Templates;

const root = createRoot(document.getElementById('sample'));
root.render(<Templates />);
