index.css:
.fab-appearence-container {
    width: 80%;
    margin: 8% auto;
    text-align: center;
    max-width: 800px;
}
.custom-index {
    z-index: 500 !important;
}

.fab-appearence-container .col-xs-12 {
    position: relative;
    margin: 15px 0;
    min-height: 100px;
}

.fab-appearence-container .col-md-6 {
    padding: 0;
}

@media only screen and (max-width: 600px) {
    .fab-appearence-container .col-xs-12 {
        min-height: 110px;
    }
}

/*start of hover css*/
.e-fab.e-btn.fab-hover {
    padding: 0 10px;
}

.fab-hover .textEle {
    margin-left: 5px;
    float: right;
}

.fab-hover .text-container {
    overflow: hidden;
    width: 0;
    margin: 0;
    transition: width .5s linear 0s, margin .2s linear .5s;
}

.fab-hover:hover .text-container {
    width: 70px;
    margin: 0 5px;
    transition: width .5s linear .2s, margin .2s linear 0s;
}

/*end of hover css*/
/*start of mobile view css*/
.e-bigger .e-btn.fab-hover .e-btn-icon.e-icon-left,
.e-btn.fab-hover .e-btn-icon.e-icon-left {
    margin-left: 0;
    width: 1em;
}

.e-bigger .fab-hover:hover .text-container {
    width: 75px;
}

body[class*="material3"].e-bigger .fab-hover:hover .text-container,
body[class*="material3-dark"].e-bigger .fab-hover:hover .text-container {
    width: 65px;
}

.e-bigger .e-fab.e-btn.fab-hover {
    padding: 0 13px;
}

.bootstrap4 .e-bigger .e-btn.fab-hover .e-btn-icon.e-icon-left,
.bootstrap4 .e-btn.fab-hover .e-btn-icon.e-icon-left {
    padding: 0;
}
/*end of mobile view css*/

/* Represents the styles for icon */
@font-face {
    font-family: 'fab-icons';
    src: url(data:application/x-font-ttf;charset=utf-8;base64,) format('truetype');
    font-weight: normal;
    font-style: normal;
}

[class^="fab-icon-"],
[class*=" fab-icon-"] {
    font-family: 'fab-icons' !important;
    speak: none;
    font-size: 55px;
    font-style: normal;
    font-weight: normal;
    font-variant: normal;
    text-transform: none;
    line-height: inherit;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
}

.fab-icon-shopping::before {
    content: "\e70c";
}

.e-fab.e-btn .e-btn-icon {
    margin-top: 5px !important;
}

