import { createRoot } from 'react-dom/client';
import './index.css';
import * as React from 'react';
import { useEffect } from 'react';
import { GanttComponent, Inject, Selection, ColumnsDirective, ColumnDirective } from '@syncfusion/ej2-react-gantt';
import { DataManager, WebApiAdaptor } from '@syncfusion/ej2-data';

const RemoteData = () => {
    const taskFields = {
        id: 'TaskId',
        name: 'TaskName',
        startDate: 'StartDate',
        duration: 'Duration',
        dependency: 'Predecessor',
        child: 'SubTasks'
    };
    const projectStartDate = new Date('02/24/2021');
    const projectEndDate = new Date('06/10/2021');
    const gridLines = 'Both';
    const timelineSettings = {
        timelineUnitSize: 50,
        topTier: {
            unit: 'Month',
            format: 'MMM dd, y',
        },
        bottomTier: {
            unit: 'Day',
            formatter: (date) => {
                let month = date.getMonth();
                if (month === 1) {
                    return '';
                }
                else {
                    let presentDate = new Date(date.getFullYear(), date.getMonth(), date.getDate());
                    let start = new Date(presentDate.getFullYear(), 0, 0);
                    let diff = Number(presentDate) - Number(start);
                    let oneDay = 1000 * 60 * 60 * 24;
                    let day = Math.floor(diff / oneDay);
                    return 'day ' + (day - 59);
                }
            },
        },
    };
    const dataSource = new DataManager({
        url: 'http://localhost:62728/api/GanttData',
        adaptor: new WebApiAdaptor,
        crossDomain: true
    });
    const labelSettings = {
        leftLabel: 'TaskName'
    };
    return (<div className='control-pane'>
      <div className='control-section'>
        <GanttComponent id='RemoteData' dataSource={dataSource} allowSorting={true} dateFormat={'MMM dd, y'} treeColumnIndex={1} allowSelection={true} highlightWeekends={false} includeWeekend={true} allowUnscheduledTasks={true} projectStartDate={projectStartDate} projectEndDate={projectEndDate} taskFields={taskFields} gridLines={gridLines} timelineSettings={timelineSettings} labelSettings={labelSettings} height='410px'>
          <ColumnsDirective>
            <ColumnDirective field='TaskId' visible={false}></ColumnDirective>
            <ColumnDirective field='TaskName' headerText='Task Name' width='250' clipMode='EllipsisWithTooltip'></ColumnDirective>
            <ColumnDirective field='StartDate'></ColumnDirective>
            <ColumnDirective field='Duration'></ColumnDirective>
          </ColumnsDirective>
          <Inject services={[Selection]}/>
        </GanttComponent>
        <div style={{ float: 'right', margin: '10px' }}>Source:
          <a href="https://en.wikipedia.org/wiki/Cereal_growth_staging_scales" target='_blank'>https://en.wikipedia.org/</a>
        </div>
      </div>

    </div>);
};
export default RemoteData;

const root = createRoot(document.getElementById('sample'));
root.render(<RemoteData />);
