import { createRoot } from 'react-dom/client';
import './index.css';
import * as React from 'react';
import { ImageEditorComponent } from '@syncfusion/ej2-react-image-editor';
import { MultiSelectComponent, CheckBoxSelection } from '@syncfusion/ej2-react-dropdowns';
import { DropDownButtonComponent } from '@syncfusion/ej2-react-splitbuttons';
import { NumericTextBoxComponent } from '@syncfusion/ej2-react-inputs';
import { SampleBase } from './sample-base';

MultiSelectComponent.Inject(CheckBoxSelection);
export class FileRestrict extends SampleBase {
    imgObj;
    minFileSize = 1;
    maxFileSize = 100;
    allowedExtensions = ".jpeg, .jpg, .png, .svg, .webp";
    fileSizeUnit = 'MB';
    fileExtensionsList = [
        { Name: "JPEG", Value: ".jpeg" },
        { Name: "JPG", Value: ".jpg" },
        { Name: "PNG", Value: ".png" },
        { Name: "SVG", Value: ".svg" },
        { Name: "WebP", Value: ".webp" }
    ];
    units = [
        { text: "MB" },
        { text: "KB" },
    ];
    convertToBytes(value) {
        return value * (this.fileSizeUnit === "MB" ? 1024 * 1024 : 1024);
    }
    updateUploadSettings = () => {
        if (this.imgObj) {
            this.imgObj.uploadSettings = {
                minFileSize: this.convertToBytes(this.minFileSize),
                maxFileSize: this.convertToBytes(this.maxFileSize),
                allowedExtensions: this.allowedExtensions
            };
            this.imgObj.dataBind();
        }
    };
    onSelect = (e) => {
        this.fileSizeUnit = e.item.text;
        this.updateUploadSettings();
    };
    beforeItemRender = (e) => {
        if (e.itemData.text === this.fileSizeUnit) {
            e.element.classList.add('e-selected');
        }
    };
    onCreated = () => {
        this.updateUploadSettings();
    };
    render() {
        return (<div className="control-pane">
                <div className="control-section">
                    <div className="row">
                        <div className="col-lg-12 control-section">
                            <div style={{ textAlign: 'center' }}>
                                <div className="e-img-editor-dropdown-container">
                                <label>Size As</label><br />
                                    <DropDownButtonComponent id="dropdownbtn" items={this.units} content={this.fileSizeUnit} select={this.onSelect} beforeItemRender={this.beforeItemRender}/>
                                </div>
                                <div className="e-img-editor-numeric-textbox-container">
                                    <label>Minimum Size</label>
                                    <br />
                                    <NumericTextBoxComponent value={this.minFileSize} min={this.minFileSize} width="180px" change={(e) => {
                this.minFileSize = e.value;
                this.updateUploadSettings();
            }}/>
                                </div>
                                <div className="e-img-editor-numeric-textbox-container">
                                    <label>Maximum Size</label>
                                    <span> (in Bytes)</span>
                                    <br />
                                    <NumericTextBoxComponent value={this.maxFileSize} min={this.minFileSize} width="180px" change={(e) => {
                this.maxFileSize = e.value;
                this.updateUploadSettings();
            }}/>
                                </div>
                                <div className="e-img-editor-dropdown-container">
                                    <label>Allowed File Extensions</label>
                                    <br />
                                    <MultiSelectComponent dataSource={this.fileExtensionsList} fields={{ text: 'Name', value: 'Value' }} mode="CheckBox" showSelectAll={true} value={this.allowedExtensions.split(', ')} change={(e) => {
                if (e.value.length === 0) {
                    this.allowedExtensions = ".jpeg, .jpg, .png, .svg, .webp";
                }
                else {
                    this.allowedExtensions = e.value.join(', ');
                }
                this.updateUploadSettings();
            }}/>
                                </div>
                            </div>
                            <div className="e-img-editor-sample">
                                <ImageEditorComponent id="image-editor" ref={(img) => { this.imgObj = img; }} uploadSettings={{
                minFileSize: this.convertToBytes(this.minFileSize),
                maxFileSize: this.convertToBytes(this.maxFileSize),
                allowedExtensions: this.allowedExtensions
            }} created={this.onCreated}/>
                            </div>
                        </div>
                    </div>
                </div>
            </div>);
    }
}

const root = createRoot(document.getElementById('sample'));
root.render(<FileRestrict />);

