@font-face {
    font-family: 'swap-icon';
    src: url(data:application/x-font-ttf;charset=utf-8;base64,) format('truetype');
    font-weight: normal;
    font-style: normal;
}
.e-swap-icon {
    font-family: 'swap-icon' !important;
    speak: none;
    font-size: 40px;
    font-style: normal;
    font-weight: normal;
    font-variant: normal;
    text-transform: none;
    line-height: 1;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
    align-self: center;
    margin: 0 10px;
    transform: rotateX(180deg);
}
#drag-drop-wrapper .e-swap-icon:before {
    content: '\e700';
}
#drag-drop-wrapper {
    width: 85%;
    margin: auto;
    display: flex;
}
#drag-drop-wrapper .e-btn {
    margin-top: 15px;
    float: right;
    margin-bottom: -43px;
}
.e-bigger #drag-drop-wrapper .e-btn {
    margin-bottom: -51px;
}
.listbox-control {
    width: 40%;
    margin: auto;
}
@media screen and (max-width: 590px) {
    .listbox-control,
    #drag-drop-wrapper {
        width: 100%;
    }
    .e-swap-icon {
        font-size: 30px;
    }
}
#right-pane {
    transform: none !important;
}

