import { createRoot } from 'react-dom/client';
import './index.css';
import * as React from 'react';
import { useEffect, useRef } from 'react';
import { ListBoxComponent } from '@syncfusion/ej2-react-dropdowns';
import { DataManager } from '@syncfusion/ej2-data';

import * as data from './dataSource.json';

const DragAndDrop = () => {
    const listObj1 = useRef(null);
    const listObj2 = useRef(null);
    const dataA = new DataManager({
        json: data["dragAndDropA"]
    });
    const dataB = new DataManager({
        json: data["dragAndDropB"]
    });
    const fields = { text: 'Name' };
    const modifiedDataA = { addedRecords: [], deletedRecords: [], changedRecords: [] };
    const modifiedDataB = { addedRecords: [], deletedRecords: [], changedRecords: [] };
    const saveChanges = () => {
        dataA.saveChanges(modifiedDataA, fields.text);
        dataB.saveChanges(modifiedDataB, fields.text);
        modifiedDataA.addedRecords = [];
        modifiedDataB.addedRecords = [];
    };
    const onDropGroupA = (args) => {
        args.items.forEach((item) => {
            if (!listObj1.current.getDataByValue(item[fields.text])) { /*Preventing item manipulation on drag and drop within same list box.*/
                modifiedDataB.addedRecords.push(item);
                modifiedDataA.deletedRecords.push(item);
            }
        });
    };
    const onDropGroupB = (args) => {
        args.items.forEach((item) => {
            if (!listObj2.current.getDataByValue(item[fields.text])) {
                modifiedDataA.addedRecords.push(item);
                modifiedDataB.deletedRecords.push(item);
            }
        });
    };
    return (<div className='control-pane'>
            <div className='col-lg-12 control-section' style={{ minHeight: '450px' }}>
                <div id="drag-drop-wrapper">
                    <div className="listbox-control">
                        <h4>Group A</h4>
                        <ListBoxComponent ref={listObj1} dataSource={dataA} scope="combined-list" height="330px" allowDragAndDrop={true} fields={fields} drop={onDropGroupA}/>
                    </div>
                    <span className="e-swap-icon"></span>
                    <div className="listbox-control">
                        <h4>Group B</h4>
                        <ListBoxComponent ref={listObj2} dataSource={dataB} scope="combined-list" height="330px" allowDragAndDrop={true} fields={fields} drop={onDropGroupB}/>
                        <button className="e-btn" onClick={saveChanges}>Update</button>
                    </div>
                </div>
            </div>
        </div>);
};
export default DragAndDrop;

const root = createRoot(document.getElementById('sample'));
root.render(<DragAndDrop />);
