index.css:
#template-listbox-control {
    width: 350px;
    margin: auto;
    box-sizing: border-box;
}
#template-listbox-control .e-listbox-wrapper {
    margin: auto;
    max-width: 400px;
    box-sizing: border-box;
} 
#template-listbox-control .e-listbox-wrapper:not(.e-list-template) .e-list-item {
    padding: 0;
    position: unset;
    cursor: pointer;
    height: 76px;
    line-height: normal;
} 
#template-listbox-control .list-wrapper {
      height: inherit;
      position: relative;
      padding: 14px 12px 14px 78px;
} 
#template-listbox-control .list-wrapper .text,
#template-listbox-control .list-wrapper .description {
    display: block;
    margin: 0;
    padding-bottom: 3px; 
    white-space: normal;
}  
#template-listbox-control .list-wrapper .description {
    font-size: 12px;
    font-weight: 500;
} 
#template-listbox-control .e-listbox-wrapper .list-wrapper .text {
    font-weight: bold;
    font-size: 13px;
} 
#template-listbox-control .list-wrapper .e-avatar {
      position: absolute;
      left: 5px;
      background-color: transparent;
      font-size: 22px;
      top: calc(50% - 27px);
} 
#template-listbox-control .javascript {
    background-image: url('https://ej2.syncfusion.com/react/demos/src/list-box/images/javascript.svg');
    width: 50px;
    height: 50px;
    background-repeat: no-repeat;
    margin-left: 10px;
} 
#template-listbox-control .typescript {
    background-image: url('https://ej2.syncfusion.com/react/demos/src/list-box/images/typescript.svg');
    width: 50px;
    height: 50px;
    background-repeat: no-repeat;
    margin-left: 10px;
} 
#template-listbox-control .angular {
    background-image: url('https://ej2.syncfusion.com/react/demos/src/list-box/images/angular.svg');
    width: 50px;
    height: 50px;
    background-repeat: no-repeat;
    margin-left: 10px;
} 
#template-listbox-control .vue {
    background-image: url('https://ej2.syncfusion.com/react/demos/src/list-box/images/vue.svg');
    width: 50px;
    height: 50px;
    background-repeat: no-repeat;
    margin-left: 10px;
} 
#template-listbox-control .react {
    background-image: url('https://ej2.syncfusion.com/react/demos/src/list-box/images/react.svg');
    width: 50px;
    height: 50px;
    background-repeat: no-repeat;
    margin-left: 10px;
}
@media screen and (max-width: 590px) {
    #template-listbox-control {
        width: 100%;
    }
}

index.js:
import { createRoot } from 'react-dom/client';
import './index.css';
import * as React from 'react';
import { ListBoxComponent } from '@syncfusion/ej2-react-dropdowns';
import { SampleBase } from './sample-base';
import * as data from './dataSource.json';

export class Template extends SampleBase {
    data = data["template_data"];
    render() {
        return (<div className='control-pane'>
                <div className='col-lg-12 control-section'>
                    <div id="template-listbox-control">
                        <ListBoxComponent dataSource={this.data} itemTemplate='<div class="list-wrapper"><span class="${pic} e-avatar e-avatar-xlarge e-avatar-circle"></span><span class="text">${text}</span><span class="description">${description}</span></div>'/>
                    </div>
                </div>
            </div>);
    }
}

const root = createRoot(document.getElementById('sample'));
root.render(<Template />);

