index.css:
#sample-list {
    max-width: 500px;
}
#sample-list,#sample-list-flat,#sample-list-group {
    border: 1px solid #dddddd;
    border-radius: 3px;
    margin: auto;    
}
#flat-list, #group-list {
    width: 50%;
    padding: 10px;
    margin: auto;
}
#flat-list {
    float: left;
}
#group-list {
    float: right;
}
#flat-list .displayText, #group-list .displayText {
    padding-left: 15px;
    font-size: 18px;
    font-weight: 500;
    line-height: 1.1;
    margin-top: 10px;
    margin-bottom: 10px;
}
@media (max-width: 590px) {
    #flat-list, #group-list {
        width: 100%;
    }
}
.descriptionLayout{
    clear: both;
}

#listview-scrolling .displayText {
    text-align: center;
    padding-bottom: 10px;
    font-size: 18px;
    font-weight: 500;
    line-height: 1.1;
    margin-top: 10px;
    margin-bottom: 10px;
}

.bootstrap5 #listview-scrolling .rating-content .e-rating-container .e-rating-item-container.e-rating-selected .e-rating-icon,
.bootstrap5-dark #listview-scrolling .rating-content .e-rating-container .e-rating-item-container.e-rating-selected .e-rating-icon,
.bootstrap5_3  #listview-scrolling .rating-content .e-rating-container .e-rating-item-container.e-rating-selected .e-rating-icon,
.bootstrap5_3-dark #listview-scrolling .rating-content .e-rating-container .e-rating-item-container.e-rating-selected .e-rating-icon {
    background: darkblue;
    background-clip: text;
}

index.js:
import { createRoot } from 'react-dom/client';
import './index.css';
/**
 * ListView Remote Sample
 */
import * as React from 'react';
import { useEffect } from 'react';
import { ListViewComponent } from '@syncfusion/ej2-react-lists';

import { DataManager, Query } from '@syncfusion/ej2-data';

const Remote = () => {
    //Initialize dataSource with the DataManager instance.
    let dataSource = new DataManager({
        url: 'http://localhost:62728/api/',
        crossDomain: true
    });
    //Initialize query with the Query instance to get specified set of data
    let query = new Query().from('ListView').select('EmployeeID,FirstName').take(10);
    //Map the appropriate columns to fields property
    let fields = {
        id: 'EmployeeID',
        text: 'FirstName'
    };
    return (<div className='control-pane'>
           <div className='control-section'>
               {/* ListView element */}
               <ListViewComponent id='sample-list' dataSource={dataSource} fields={fields} query={query} headerTitle='Employees' showHeader={true}></ListViewComponent>
           </div>
       </div>);
};
export default Remote;

const root = createRoot(document.getElementById('sample'));
root.render(<Remote />);
