index.css:
#disabledMention {
    min-height: 100px;
    border: 1px solid #D7D7D7;
    width: 600px;
    padding: 8px;
    font-size: 14px;
    border-radius: 4px;
}
#mention_disabled {
    margin: 0 auto;
    width: 600px;
}
.disabled_listItems {
    padding-left: 12px;
    vertical-align: middle;
    width: 80%;
}
#disabledMention_popup .e-dropdownbase .e-list-item {
    line-height: 25px;
    text-indent: 8px;
}
#disabledMention_popup .mentionEmpImage {
    width: 44px;
    float: left;
}
#disabledMention_popup .person {
    font-size: 16px;
    margin-top: 8px;
}
#disabledMention_popup .e-dropdownbase .e-list-item * {
    display: block;
}
div#disabledMention[placeholder]:empty:before {
    content: attr(placeholder);
    color: #555;
}
#mention_disabled .disabled-size {
    font-size: 15px;
    font-weight: 600;
}

@media screen and (max-width: 1010px) {
    #disabledMention,
    #mention_disabled, #mention_disabled table{
        width: 100%;
    }
}

index.js:
import { createRoot } from 'react-dom/client';
import './index.css';
import * as React from 'react';
import { MentionComponent } from '@syncfusion/ej2-react-dropdowns';
import { SampleBase } from './sample-base';

import * as data from './dataSource.json';
export class DisabledItems extends SampleBase {
    temp = 'emailData2';
    emailData = data[this.temp];
    disabledTarget = '#disabledMention';
    disabledFields = { text: 'Name', disabled: 'State' };
    itemTemplate(data) {
        return (<div className="disabled_listItems">
          <img className="mentionEmpImage" src={"https://ej2.syncfusion.com/react/demos/src/mention/Employees/" + data['Eimg'] + ".png"} alt="employee"/>
          <span className="person">{data.Name}</span>
          <span className="email">{data.EmailId}</span>
      </div>);
    }
    displayTemplate(data) {
        return (<React.Fragment>
        {data.Name}
      </React.Fragment>);
    }
    render() {
        return (<div className='control-pane'>
        <div className='control-section'>
            <div className="content-wrapper">
              <div id='mention_disabled'>
                <table>
                  <tbody>
                    <tr>
                        <td>
                            <label className="disabled-size">Compose your content</label>
                            <div id="disabledMention" placeholder="Type @ and tag user"></div>
                        </td>
                    </tr>
                  </tbody>
                </table>

                <MentionComponent dataSource={this.emailData} target={this.disabledTarget} fields={this.disabledFields} itemTemplate={this.itemTemplate} displayTemplate={this.displayTemplate} noRecordsTemplate={"No item related to the search"} popupWidth={250} popupHeight={200}></MentionComponent>

              </div>
            </div>
        </div>
      </div>);
    }
}

const root = createRoot(document.getElementById('sample'));
root.render(<DisabledItems />);

