index.css:
#templateMention {
    min-height: 100px;
    border: 1px solid #D7D7D7;
    width: 600px;
    padding: 8px;
    font-size: 14px;
    border-radius: 4px;
}
#mention_template {
    margin: 0 auto;
    width: 600px;
}
.template_listItems {
    padding-left: 12px;
    vertical-align: middle;
    width: 80%;
}
#templateMention_popup .e-dropdownbase .e-list-item {
    line-height: 25px;
    text-indent: 8px;
}
#templateMention_popup .mentionEmpImage {
    width: 44px;
    float: left;
}
#templateMention_popup .person {
    font-size: 16px;
    margin-top: 8px;
}
#templateMention_popup .e-dropdownbase .e-list-item * {
    display: block;
}
div#templateMention[placeholder]:empty:before {
    content: attr(placeholder);
    color: #555;
}
#mention_template .template-size {
    font-size: 15px;
    font-weight: 600;
}

@media screen and (max-width: 1010px) {
    #templateMention,
    #mention_template, #mention_template table{
        width: 100%;
    }
}

index.js:
import { createRoot } from 'react-dom/client';
import './index.css';
import * as React from 'react';
import { useEffect } from 'react';
import { MentionComponent } from '@syncfusion/ej2-react-dropdowns';


import * as data from './dataSource.json';
const Template = () => {
    const temp = 'emailData';
    const emailData = data[temp];
    const templateTarget = '#templateMention';
    const templateFields = { text: 'Name' };
    const itemTemplate = (data) => {
        return (<div className="template_listItems">
        <img className="mentionEmpImage" src={"https://ej2.syncfusion.com/react/demos/src/mention/Employees/" + data['Eimg'] + ".png"} alt="employee"/>
        <span className="person">{data.Name}</span>
        <span className="email">{data.EmailId}</span>
      </div>);
    };
    const displayTemplate = (data) => {
        return (<React.Fragment>
        {data.Name}
      </React.Fragment>);
    };
    return (<div className='control-pane'>
      <div className='control-section'>
        <div className='col-lg-12'>
          <div className="content-wrapper">
            <div id='mention_template'>
              <table>
                <tbody>
                  <tr>
                    <td>
                      <label className="template-size">Compose your content</label>
                      <div id="templateMention" placeholder="Begin writing here..!"></div>
                    </td>
                  </tr>
                </tbody>
              </table>

              <MentionComponent dataSource={emailData} target={templateTarget} fields={templateFields} itemTemplate={itemTemplate} displayTemplate={displayTemplate} noRecordsTemplate={"No item related to the search"} popupWidth={250} popupHeight={200}></MentionComponent>

            </div>
          </div>
        </div>
      </div>

    </div>);
};
export default Template;

const root = createRoot(document.getElementById('sample'));
root.render(<Template />);
