import * as ReactDOM from 'react-dom';
import * as React from 'react';
import { select } from '@syncfusion/ej2-base';
export class PropertyPane extends React.Component {
    render() {
        const mobilePropPane = select('.sb-mobile-prop-pane');
        const isMobile = window.matchMedia('(max-width:550px)').matches;
        return isMobile && mobilePropPane ?
            ReactDOM.createPortal(<div className='property-panel-section'>
                    <div className="property-panel-header">
                        {this.props.title}
                    </div>
                    <div className="property-panel-content">
                        {this.props.children}
                    </div>
                </div>, mobilePropPane)
            :
                (<div className='property-panel-section'>
                    <div className="property-panel-header">
                        {this.props.title}
                    </div>
                    <div className="property-panel-content">
                        {this.props.children}
                    </div>
                </div>);
    }
}

index.js:
import { createRoot } from 'react-dom/client';
import './index.css';
import * as React from 'react';
import { useEffect, useState } from 'react';
import { useCallback } from 'react';

import { MultiSelectComponent, Inject, VirtualScroll, CheckBoxSelection } from '@syncfusion/ej2-react-dropdowns';
import { DataManager, UrlAdaptor } from '@syncfusion/ej2-data';
import { CheckBoxComponent } from '@syncfusion/ej2-react-buttons';
import { PropertyPane } from './property-pane';

const Default = () => {
    // define the array of string
    let records = [];
    for (let i = 1; i <= 150; i++) {
        let item = {};
        item.id = 'id' + i;
        item.text = `Item ${i}`;
        // Generate a random number between 1 and 4 to determine the group
        const randomGroup = Math.floor(Math.random() * 4) + 1;
        switch (randomGroup) {
            case 1:
                item.group = 'Group A';
                break;
            case 2:
                item.group = 'Group B';
                break;
            case 3:
                item.group = 'Group C';
                break;
            case 4:
                item.group = 'Group D';
                break;
            default:
                break;
        }
        records.push(item);
    }
    const headerTemplate = useCallback(() => {
        return (<div className="header"><span style={{ marginLeft: '17px' }}>Items Info</span></div>);
    }, []);
    //set the value to item template
    const itemTemplate = useCallback((data) => {
        return (<div className="ename" style={{ height: '40px' }}> {data.text} </div>);
    }, []);
    //set the value to value template
    const valueTemplate = useCallback((data) => {
        return (<div className="name"> {data.text} </div>);
    }, []);
    const [allowFiltering, setAllowFiltering] = useState(true);
    const [allowCustomValue, setAllowCustomValue] = useState(true);
    const [hideSelectedItem, setHideSelectedItem] = useState(true);
    const [closePopupOnSelect, setClosePopupOnSelect] = useState(true);
    // bind the DataManager instance to dataSource property
    const customerData = new DataManager({
        url: 'http://localhost:62728/api/VirtualDropdownData',
        adaptor: new UrlAdaptor,
        crossDomain: true
    });
    const onChange = (args) => {
        // enable or disable the SelectAll in multiselect on CheckBox checked state
        setAllowFiltering(args.checked);
    };
    const onChangeHide = (args) => {
        // enable or disable the SelectAll in multiselect on CheckBox checked state
        setHideSelectedItem(args.checked);
    };
    const onChangeClose = (args) => {
        // enable or disable the SelectAll in multiselect on CheckBox checked state
        setClosePopupOnSelect(args.checked);
    };
    const onChangeCustom = (args) => {
        // enable or disable the SelectAll in multiselect on CheckBox checked state
        setAllowCustomValue(args.checked);
    };
    // maps the appropriate column to fields property
    const fields = { text: 'text', value: 'id' };
    const value = ['id10', 'id50', 'id100', "custom"];
    const customerField = { text: 'OrderID', value: 'OrderID' };
    const groupField = { groupBy: 'group', text: 'text', value: 'id' };
    return (<div id="dropdowndefault" className='control-pane'>
        <div className="control-section col-lg-8">
          <div className="multi-control-wrapper">
          <label className="h4">Local Data</label>
            <MultiSelectComponent id="local" dataSource={records} placeholder="e.g. Item 1" mode="Box" allowFiltering={allowFiltering} enableVirtualization={true} allowCustomValue={allowCustomValue} showDropDownIcon={true} hideSelectedItem={hideSelectedItem} closePopupOnSelect={closePopupOnSelect} fields={fields} popupHeight="200px">
              <Inject services={[VirtualScroll]}/>
            </MultiSelectComponent>
          </div>
          <div className="multi-control-wrapper">
          <label className="h4">Remote Data</label>
            <MultiSelectComponent id="remote" dataSource={customerData} placeholder="e.g. OrderID" mode="Delimiter" allowFiltering={allowFiltering} enableVirtualization={true} allowCustomValue={allowCustomValue} showDropDownIcon={true} hideSelectedItem={hideSelectedItem} closePopupOnSelect={closePopupOnSelect} fields={customerField} popupHeight="200px">
              <Inject services={[VirtualScroll]}/>
            </MultiSelectComponent>
          </div>
          <div className="multi-control-wrapper">
          <label className="h4">Default Values</label>
            <MultiSelectComponent id="databind" value={value} dataSource={records} placeholder="e.g. Item 1" mode="Default" allowFiltering={allowFiltering} enableVirtualization={true} allowCustomValue={allowCustomValue} showDropDownIcon={true} hideSelectedItem={hideSelectedItem} closePopupOnSelect={closePopupOnSelect} fields={fields} popupHeight="200px">
              <Inject services={[VirtualScroll]}/>
            </MultiSelectComponent>
          </div>
          <div className="multi-control-wrapper">
            <label className="h4">Grouping</label>
            <MultiSelectComponent id="group" dataSource={records} placeholder="e.g. Item 1" mode="CheckBox" allowFiltering={allowFiltering} enableSelectionOrder={false} enableVirtualization={true} allowCustomValue={allowCustomValue} showDropDownIcon={true} fields={groupField} popupHeight="200px">
              <Inject services={[VirtualScroll, CheckBoxSelection]}/>
            </MultiSelectComponent>
          </div>
          <div className="multi-control-wrapper">
          <label className="h4">Template</label>
            <MultiSelectComponent id="Template" dataSource={records} placeholder="e.g. Item 1" mode="Default" allowFiltering={allowFiltering} enableVirtualization={true} allowCustomValue={allowCustomValue} showDropDownIcon={true} hideSelectedItem={hideSelectedItem} closePopupOnSelect={closePopupOnSelect} fields={fields} popupHeight="200px" itemTemplate={itemTemplate} valueTemplate={valueTemplate} headerTemplate={headerTemplate}>
              <Inject services={[VirtualScroll]}/>
            </MultiSelectComponent>
          </div>
        </div>
        <div className='col-lg-4 property-section' style={{ left: '0px', width: '250px' }}>
          <PropertyPane title='Properties'>
            <table id="property" title="Properties" className='property-panel-table' style={{ width: '100%' }}>
              <tr>
                <td>
                  <div>
                    <CheckBoxComponent checked={true} label='AllowFiltering' change={onChange.bind(this)}></CheckBoxComponent>
                  </div>
                </td>
              </tr>
              <tr>
                <td>
                  <div>
                    <CheckBoxComponent checked={true} label='AllowCustomValue' change={onChangeCustom.bind(this)}></CheckBoxComponent>
                  </div>
                </td>
              </tr>
              <tr>
                <td>
                  <div>
                    <CheckBoxComponent checked={true} label='HideSelectedItem' change={onChangeHide.bind(this)}></CheckBoxComponent>
                  </div>
                </td>
              </tr>
              <tr>
                <td>
                  <div>
                    <CheckBoxComponent checked={true} label='ClosePopupOnSelect' change={onChangeClose.bind(this)}></CheckBoxComponent>
                  </div>
                </td>
              </tr>
            </table>
          </PropertyPane>
        </div>
      </div>);
};
export default Default;

const root = createRoot(document.getElementById('sample'));
root.render(<Default />);
