index.css:
.control-wrapper.template-multicolumn {
    margin: 0 auto;
    width: 250px;
}

@media screen and (max-width: 480px) {
    .control-wrapper.template-multicolumn {
        width: 300px;
    }
}

/* template styles */
.multicolumn-customize .e-headercell.e-templatecell {
    height: 40px;
}

.multicolumn-customize .header {
    overflow: hidden;
    text-overflow: ellipsis;
}

.multicolumn-customize .ename {
    opacity: .87;
    font-size: 16px;
    margin-top: 8px;
}

.multicolumn-customize .country {
    opacity: .87;
    font-size: 16px;
}

.multicolumn-customize .job {
    opacity: .54;
    font-size: 14px;
    margin-top: 15px;
    margin-bottom: 7px;
}

.multicolumn-customize .empImage {
    margin: 6px 16px;
    float: left;
    width: 50px;
    height: 50px;
}

@font-face {
    font-family: 'ej2-multicolumntemplate';
    src:
    url(data:application/x-font-ttf;charset=utf-8;base64,) format('truetype');
    font-weight: normal;
    font-style: normal;
}

.e-multicolumn-calender::before { 
    font-family: 'ej2-multicolumntemplate';
    content: "\e960";
}

.e-multicolumn-userlogin:before { 
    font-family: 'ej2-multicolumntemplate';
    content: "\e702";
}

.e-multicolumn-calender,
.e-multicolumn-userlogin {
    margin-right: 5px;
}

index.js:
import { createRoot } from 'react-dom/client';
import './index.css';
import * as React from 'react';
import { MultiColumnComboBoxComponent, ColumnsDirective, ColumnDirective } from '@syncfusion/ej2-react-multicolumn-combobox';
import { useEffect } from 'react';


import * as data from './dataSource.json';
const Template = () => {
    const fields = { text: 'Name', value: 'Eimg' };
    return (<div className='control-pane'>
            <div className="control-section">
                <div className='control-wrapper template-multicolumn'>
                    <div style={{ paddingTop: '55px' }}>
                    <label>Select an employee</label>
                        <MultiColumnComboBoxComponent cssClass='multicolumn-customize' type="text" dataSource={data.empList} fields={fields} placeholder='e.g. Andrew Fuller' popupHeight={'230px'} popupWidth={'540px'} gridSettings={{ rowHeight: 40 }}>
                            <ColumnsDirective>
                                <ColumnDirective field='Eimg' header='Photos' width={90} headerTemplate={'<div class="header"> <span>Photo</span> </div>'} template={'<div><img class="empImage" src="https://ej2.syncfusion.com/react/demos/src/multicolumn-combobox/Employees/${Eimg}.png" alt="employee"/> </div>'}></ColumnDirective>
                                <ColumnDirective field='Name' header='Employee Name' width={160} headerTemplate={'<div class="header"> <span class="e-icons e-multicolumn-userlogin"></span> <span>Employee info</span> </div>'} template={'<div class="ename"> ${Name} </div>' + '<div class="job"> ${Designation} </div>'}></ColumnDirective>
                                <ColumnDirective field='DateofJoining' header='Date Of Joining' width={130} headerTemplate={'<div class="header"> <span class="e-icons e-multicolumn-calender"></span> <span>Date of joining</span> </div>'} template={'<div class="dateOfJoining"> ${DateofJoining} </div>'}></ColumnDirective>
                                <ColumnDirective field='Country' header='Country' width={100} headerTemplate={'<div class="header"> <span>Country</span> </div>'} template={'<div class="country"> ${Country} </div>'}></ColumnDirective>
                            </ColumnsDirective>
                        </MultiColumnComboBoxComponent>
                    </div>
                </div>
            </div>


        </div>);
};
export default Template;

const root = createRoot(document.getElementById('sample'));
root.render(<Template />);
