}

.e-pv-e-sign-user-dropdown .e-input-group-icon.e-ddl-icon.e-search-icon {
    padding: 0px ;
}

.e-pv-sign-hide-toolbar {
    display: none;
}

.e-pv-sign-show-toolbar {
    display: block;
