}

.e-custom-search-input {
    width: 234px;
}

.e-text-search-popup .e-footer-content,
.e-bookmark-popup .e-footer-content {
    padding: 0;
    height: 0;
}

.search-button,
.search-button:disabled,
.search-button:focus,
.search-button:hover {
    background: transparent;
    box-shadow: none;
}

#popup .e-dlg-content {
    padding-left: 0;
    padding-bottom: 0;
}

.e-pv-bookmarks {
    min-width: 234px;
}

.e-pv-current-page-number {
    width: 46px;
    height: 28px;
    text-align: center;
}

.material .e-pv-current-page-number {
    border-width: 1px;
}

.e-btn-icon.e-pv-zoom-out-icon.e-icons {
    transform: rotate(90deg);
}

.flex-container {
    display: flex;
    justify-content: flex-end;
    align-items: center;
}

.e-pv-flex-container{
    display: flex;
