    align-items: center;
    position: sticky;
    top: 0px;
    z-index: 2;
    background-color: white;
    border-bottom: 1px solid #D7D7D7;
    padding-bottom: 5px;
    margin-bottom: 16px;
}

.e-pv-annot-inner-table td {
    text-wrap: nowrap;
    padding: 12px 12px 0 12px;
    width: 50%;
}

.e-pv-main-panel {
    border-left: 1px solid #D7D7D7;
