import { createRoot } from 'react-dom/client';
import './index.css';
import * as React from 'react';
import { DialogComponent } from '@syncfusion/ej2-react-popups';
import { GridComponent, ColumnsDirective, ColumnDirective, CommandColumn } from '@syncfusion/ej2-react-grids';
import { SampleBase } from './sample-base';
import { gridData } from './data';
import { PdfViewerComponent, Toolbar, Magnification, Navigation, LinkAnnotation, BookmarkView, ThumbnailView, Print, TextSelection, TextSearch, Annotation, FormFields, FormDesigner, PageOrganizer, Inject } from '@syncfusion/ej2-react-pdfviewer';
import { SwitchComponent } from '@syncfusion/ej2-react-buttons';

export class DocumentList extends SampleBase {
    dialogInstance;
    viewer;
    gridInstance;
    commands = [{ type: 'View', buttonOption: { cssClass: 'e-icons e-eye e-flat', title: 'View' } },
        { type: 'Edit', buttonOption: { cssClass: 'e-icons e-edit e-flat', title: 'Edit' } }];
    constructor(props) {
        super(props);
        this.state = {
            hideDialog: true
        };
    }
    change = (args) => {
        if (args.checked) {
            this.viewer.serviceUrl = '';
        }
        else {
            this.viewer.serviceUrl = 'http://localhost:62728/api/pdfviewer';
        }
    };
    dialogClose() {
        this.setState({ hideDialog: true });
    }
    dialogOpen() {
        this.setState({ hideDialog: false });
    }
    destroyed() {
        this.viewer.destroy();
        this.dialogInstance.destroy();
    }
    buttonClick(args) {
        this.dialogOpen();
        var viewerContainer = document.getElementById('container').ej2_instances[0];
        viewerContainer.documentPath = args.rowData.Url;
        if (args.commandColumn.buttonOption.title == 'View') {
            viewerContainer.textFieldSettings = {
                isReadOnly: true,
