    function IsRGBAColor(input) {
        const rgbaPattern = /^rgba\((\d+),\s*(\d+),\s*(\d+),\s*(\d+(?:\.\d+)?)\)$/;
        if (input !== null) {
            return rgbaPattern.test(input);
        }
        return false;
    }
