import { createRoot } from 'react-dom/client';
import './index.css';
import * as React from 'react';
import { PivotViewComponent, VirtualScroll, Inject, FieldList, GroupingBar } from '@syncfusion/ej2-react-pivotview';

import { Browser } from '@syncfusion/ej2-base';

/**
 * PivotView Server Side Engine Sample.
 */
let pivotObj;
let dataSourceSettings = {
    url: 'http://localhost:62728/api/pivot/post',
    mode: 'Server',
    expandAll: false,
    enableSorting: true,
    columns: [{ name: 'Year', caption: 'Production Year' },
    ],
    values: [
        { name: 'Sold', caption: 'Units Sold' },
        { name: 'Price', caption: 'Sold Amount' }
    ],
    rows: [{ name: 'ProductID', caption: 'Product ID' }],
    formatSettings: [{ name: 'Price', format: 'C0' }, { name: 'Sold', format: 'N0' }],
    filters: []
};
function ServerSideEngine() {
    function onDataBound() {
        if (Browser.isDevice && pivotObj && pivotObj.enableRtl) {
            document.querySelector('.control-section').classList.add('e-rtl');
        }
    }
    return (<div className='control-pane'>
            <div className='control-section'>
                <PivotViewComponent id='PivotView' ref={d => pivotObj = d} dataSourceSettings={dataSourceSettings} showFieldList={true} showGroupingBar={true} width={'100%'} height={'450'} dataBound={onDataBound} enableVirtualization={true} allowDataCompression={true}>
                    <Inject services={[VirtualScroll, FieldList, GroupingBar]}/>
                </PivotViewComponent>
            </div>
        </div>);
}
export default ServerSideEngine;

const root = createRoot(document.getElementById('sample'));
root.render(<ServerSideEngine />);
