import { createRoot } from 'react-dom/client';
import './index.css';
/**
 * Sample for MultiLevel Labels Range Navigator
 */
import * as React from "react";
import { Inject, DateTime, RangeTooltip, RangeNavigatorComponent } from '@syncfusion/ej2-react-charts';
import { Browser } from '@syncfusion/ej2-base';
import { SampleBase } from './sample-base';
export let data = [];
export let value = 0;
export let point = {};
for (let j = 1; j < 1090; j++) {
    value += (Math.random() * 10 - 5);
    value = value < 0 ? Math.abs(value) : value;
    point = { x: new Date(2000, 0, j), y: value, z: value + 10 };
    data.push(point);
}
const SAMPLE_CSS = `
    .control-fluid {
		padding: 0px;
    }
    #days {
        font-size: 15px;
        font-style: normal;
        font-family: "Segoe UI";
        font-weight: 500;
        text-anchor: middle;
        transform: none;
        opacity: 1;
    }
    `;
export class MultilevelLabels extends SampleBase {
    render() {
        return (<div className='control-pane'>
                <style>
                    {SAMPLE_CSS}
                </style>
                <div className='control-section'>
                <div className="row" style={{ textAlign: "center" }}>
                <div id="days">Multi Level Labels</div>
                </div>
                <div className="row">
                 <RangeNavigatorComponent id='rangenavigator' style={{ textAlign: "center" }} labelPosition='Outside' valueType='DateTime' tooltip={{ enable: true, displayMode: 'Always' }} intervalType='Quarter' enableGrouping={true} animationDuration={500} groupBy='Years' load={this.load.bind(this)} dataSource={data} xName='x' yName='y' value={[new Date('2001-01-01'), new Date('2002-01-01')]} width={Browser.isDevice ? '100%' : '80%'}>
                    <Inject services={[DateTime, RangeTooltip]}/>
                    </RangeNavigatorComponent>
                    </div>
            </div>
        </div>);
    }
    load(args) {
        let selectedTheme = location.hash.split('/')[1];
        selectedTheme = selectedTheme ? selectedTheme : 'Fluent2';
        args.rangeNavigator.theme = (selectedTheme.charAt(0).toUpperCase() + selectedTheme.slice(1)).
            replace(/-dark/i, "Dark").replace(/contrast/i, 'Contrast').replace(/-highContrast/i, 'HighContrast');
        args.rangeNavigator.dateTimeModule = new DateTime(args.rangeNavigator);
    }
    ;
}

const root = createRoot(document.getElementById('sample'));
root.render(<MultilevelLabels />);

