index.css:
#resize-pictureList {
  display: none;
  border: 1px;
  box-shadow: 0 0 2px;
}

#pictureddl-popup #resize-pictureList {
  display: block;
}

p.ribbonResizeDisplayText {
  margin: 0 15px;
  font-size: 18px;
  font-weight: 500;
  line-height: 1.1;
}

.e-bigger #resize-pictureList .e-list-header {
  background-color: white;
 }

 body[class*="dark"].e-bigger #resize-pictureList .e-list-header,
 body[class*="high"].e-bigger #resize-pictureList .e-list-header {
     background-color: #212529;
 }

 .e-bigger #resize-pictureList ul {
     padding: 0px;
     margin-top: 0px;
 }

#ribbonContainer{
  padding: 20px;
  background-color: #dbdada;
  display: flex;
  flex-direction: column;
  height: 550px;
}

#ribbon {
  box-sizing: border-box;
}

#ribbonPlaceHolder {
  flex-grow: 1; 
  box-sizing: border-box;
  background-color: transparent;
  display: flex;
  flex-wrap: wrap;
  gap: 10px;
  margin-top: 15px;
}

/* content customization */
#ribbonPlaceHolder .content1 {
  background-color: #3a9ae5;
  width: 150px;
  height: 50%;
}

#ribbonPlaceHolder .content2 {
  background-color: #9d9c9a;
  width: calc(100% - 160px);
  height: 50%;
}

#ribbonPlaceHolder .content3 {
  background-color: #7f7e7d;
  width: 150px;
  height: calc(50% - 60px);
}

#ribbonPlaceHolder .content4 {
  background-color: #c8c6c4;
  width: calc(100% - 160px);
  height: calc(50% - 60px);
}

#ribbonPlaceHolder #toast .e-toast-close-icon {
  position: absolute;
  top: 50%;
  right: 2px ;
  transform: translateY(-50%);
  margin: 0;
}

#ribbonPlaceHolder #toast .e-toast-content {
  padding-right: 25px;
}

#ribbonPlaceHolder .e-slider-container{
  height: 2px;
}

#ribbonSlider .e-slider-track {
  background: #848587;
}

body[class*="dark"] #ribbonContainer,
body[class*="high"] #ribbonContainer {
  background: #a7a7a742;
}

.font-group .e-ribbon-group-content {
  justify-content: center;
}

#ribbonContainer #header_footer .e-ribbon-group-overflow-ddb {
  min-width: 10ch;
}

/* Represents the styles for icons */
@font-face {
font-family: 'ribbon';
src:
url(data:application/x-font-ttf;charset=utf-8;base64,) format('truetype');
font-weight: normal;
font-style: normal;
}

[class*="e-ribbon-"] [class^="sf-icon-"], [class*="e-ribbon-"] [class*=" sf-icon-"] {
font-family: 'ribbon' !important;
speak: none;
font-style: normal;
font-weight: normal;
font-variant: normal;
text-transform: none;
line-height: 1;
-webkit-font-smoothing: antialiased;
-moz-osx-font-smoothing: grayscale;
}

[class*="e-ribbon-"] .sf-icon-3d-model:before { content: "\e700"; }
[class*="e-ribbon-"] .sf-icon-shapes:before { content: "\e701"; }
[class*="e-ribbon-"] .sf-icon-dictate:before { content: "\e702"; }
[class*="e-ribbon-"] .sf-icon-chart:before { content: "\e703"; }
[class*="e-ribbon-"] .sf-icon-screenshot:before { content: "\e704"; }
[class*="e-ribbon-"] .sf-icon-smart-art:before { content: "\e706"; }
[class*="e-ribbon-"] .sf-icon-share:before { content: "\e707"; }
[class*="e-ribbon-"] .sf-icon-read:before { content: "\e708"; }
[class*="e-ribbon-"] .sf-icon-web-layout:before { content: "\e709"; }
[class*="e-ribbon-"] .sf-icon-mode:before { content: "\e70b"; }
[class*="e-ribbon-"] .sf-icon-draft:before { content: "\e70c"; }
[class*="e-ribbon-"] .sf-icon-reuse:before { content: "\e70f"; }
[class*="e-ribbon-"] .sf-icon-editor:before { content: "\e70a"; }
[class*="e-ribbon-"] .sf-icon-word:before { content: "\e70d"; }

.ribbon-resize.e-rtl + #ribbonPlaceHolder .ribbonSliderWrapper{
  direction: rtl;
}

