import { createRoot } from 'react-dom/client';
import './index.css';
import * as React from 'react';
import { useEffect } from 'react';
import { extend } from '@syncfusion/ej2-base';
import { ScheduleComponent, ViewsDirective, ViewDirective, Month, Inject, Resize, DragAndDrop } from '@syncfusion/ej2-react-schedule';


/**
 * Schedule cell template sample
 */
const CellTemplate = () => {
    const data = extend([], null, true);
    const getCellContent = (date) => {
        if (date.getMonth() === 10 && date.getDate() === 23) {
            return '<img src= "https://ej2.syncfusion.com/react/demos/src/schedule/images/thanksgiving-day.svg" alt="Thanksgiving day"/><div className="caption">Thanksgiving day</div>';
        }
        else if (date.getMonth() === 11 && date.getDate() === 9) {
            return '<img src="https://ej2.syncfusion.com/react/demos/src/schedule/images/get-together.svg" alt="Party time"/><div className="caption">Party time</div>';
        }
        else if (date.getMonth() === 11 && date.getDate() === 13) {
            return '<img src="https://ej2.syncfusion.com/react/demos/src/schedule/images/get-together.svg" alt="Party time"/><div className="caption">Party time</div>';
        }
        else if (date.getMonth() === 11 && date.getDate() === 22) {
            return '<img src="https://ej2.syncfusion.com/react/demos/src/schedule/images/birthday.svg" alt="Happy birthday"/><div className="caption">Happy birthday</div>';
        }
        else if (date.getMonth() === 11 && date.getDate() === 24) {
            return '<img src="https://ej2.syncfusion.com/react/demos/src/schedule/images/christmas-eve.svg" alt="Christmas Eve"/><div className="caption">Christmas Eve</div>';
        }
        else if (date.getMonth() === 11 && date.getDate() === 25) {
            return '<img src="https://ej2.syncfusion.com/react/demos/src/schedule/images/christmas.svg" alt="Christmas Day"/><div className="caption">Christmas Day</div>';
        }
        else if (date.getMonth() === 0 && date.getDate() === 1) {
            return '<img src="https://ej2.syncfusion.com/react/demos/src/schedule/images/newyear.svg" alt="New year"/><div className="caption">New Year\'s Day</div>';
        }
        else if (date.getMonth() === 0 && date.getDate() === 14) {
            return '<img src="https://ej2.syncfusion.com/react/demos/src/schedule/images/get-together.svg" alt="Get together"/><div className="caption">Get together</div>';
        }
        else {
            return '';
        }
    };
    const cellTemplate = (props) => {
        if (props.type === "monthCells") {
            return (<div className="templatewrap" dangerouslySetInnerHTML={{ __html: getCellContent(props.date) }}></div>);
        }
        return;
    };
    return (<div className='schedule-control-section'>
      <div className='col-lg-12 control-section'>
        <div className='control-wrapper'>
          <ScheduleComponent cssClass='cell-template' width='100%' height='650px' selectedDate={new Date(2021, 11, 15)} cellTemplate={cellTemplate} eventSettings={{ dataSource: data }}>
            <ViewsDirective>
              <ViewDirective option='Month'/>
            </ViewsDirective>
            <Inject services={[Month, Resize, DragAndDrop]}/>
          </ScheduleComponent>
        </div>
      </div>
    </div>);
};
export default CellTemplate;

const root = createRoot(document.getElementById('sample'));
root.render(<CellTemplate />);
