import { createRoot } from 'react-dom/client';
import './index.css';
import * as React from 'react';
import { useEffect, useRef } from 'react';

import { ListViewComponent, Inject, Virtualization } from '@syncfusion/ej2-react-lists';
import { SplitterComponent, PanesDirective, PaneDirective } from '@syncfusion/ej2-react-layouts';
import { Browser } from '@syncfusion/ej2-base';

/**
 *  Sample for list-view in splitter
 */
const DetailsView = () => {
    let splitterInstance = useRef(null);
    let listViewObj = useRef(null);
    let firstPaneSize = "35%";
    let firstPaneMinSize = "25%";
    let secondPaneSize = "65%";
    let secondPaneMinSize = "45%";
    let commonData = [];
    let dataSource = {};
    let liElement;
    commonData = [
        { name: 'Margaret', imgUrl: 'https://ej2.syncfusion.com/react/demos/src/splitter/images/margaret.png', id: '1' },
        { name: 'Laura', imgUrl: 'https://ej2.syncfusion.com/react/demos/src/splitter/images/laura.png', id: '2' },
        { name: 'Robert', imgUrl: 'https://ej2.syncfusion.com/react/demos/src/splitter/images/robert.png', id: '3' },
        { name: 'Albert', imgUrl: 'https://ej2.syncfusion.com/react/demos/src/splitter/images/albert.png', id: '5' },
        { name: 'Michale', imgUrl: 'https://ej2.syncfusion.com/react/demos/src/splitter/images/michale.png', id: '4' }
    ];
    [[1010, 'data1'], [5010, 'data5'], [10010, 'data10'], [25010, 'data25']].forEach((ds) => {
        let data = commonData.slice();
        dataSource[ds[1]] = data;
    });
    let template = '<div class="e-list-wrapper e-list-avatar">' +
        '<span class="e-avatar e-avatar-circle ${icon} ${$imgUrl ? \'hideUI\' : \'showUI\' }">' +
        '${icon}</span> <img class="e-avatar e-avatar-circle ${$imgUrl ? \'showUI\' : \'hideUI\' }" ' +
        'src="${imgUrl}" alt="${name}" />' +
        '<span class="e-list-content">${name}</span></div>';
    const onSelect = (e) => {
        let listid = e.item.dataset.uid;
        switch (listid) {
            case '1':
                splitterInstance.current.paneSettings[1].content = '<div class="header-image"><div class="e-avatar e-avatar-circle e-avatar-xlarge"><img src="https://ej2.syncfusion.com/react/demos/src/splitter/images/margaret.png" alt="margaret"></div></div><div class="profile-name">Margeret Peacock</div><table><tr><td class="e-bold">Title</td><td>Sales Representative</td></tr><tr><td class="e-bold">Hire Date</td><td>11/15/1994</td></tr><tr><td class="e-bold">Address</td><td>507 - 20th Ave. E. Apt. 2A</td></tr><tr><td class="e-bold">City</td><td>Seattle</td></tr><tr><td class="e-bold">Phone</td><td>(206) 555-9857</td></tr></table>';
                break;
            case '2':
                splitterInstance.current.paneSettings[1].content = '<div class="header-image"><div class="e-avatar e-avatar-circle e-avatar-xlarge"><img src="https://ej2.syncfusion.com/react/demos/src/splitter/images/laura.png" alt="laura"></div><div class="profile-name">Laura Callahan</div><table><tr><td class="e-bold">Title</td><td>Sales Representative</td></tr><tr><td class="e-bold">Hire Date</td><td>09/25/1993</td></tr><tr><td class="e-bold">Address</td><td>908 W. Capital Way</td></tr><tr><td class="e-bold">City</td><td>Tacoma</td></tr><tr><td class="e-bold">Phone</td><td>(206) 555-9482</td></tr></table>';
                break;
            case '3':
                splitterInstance.current.paneSettings[1].content = '<div class="header-image"><div class="e-avatar e-avatar-circle e-avatar-xlarge"><img src="https://ej2.syncfusion.com/react/demos/src/splitter/images/robert.png" alt="robert"></div><div class="profile-name">Robert King</div><table><tr><td class="e-bold">Title</td><td>Sales Manager</td></tr><tr><td class="e-bold">Hire Date</td><td>03/20/1990</td></tr><tr><td class="e-bold">Address</td><td>14 Garrett Hill</td></tr><tr><td class="e-bold">City</td><td>London</td></tr><tr><td class="e-bold">Phone</td><td>(71) 555-4848</td></tr></table>';
                break;
            case '4':
                splitterInstance.current.paneSettings[1].content = '<div class="header-image"><div class="e-avatar e-avatar-circle e-avatar-xlarge"><img src="https://ej2.syncfusion.com/react/demos/src/splitter/images/michale.png" alt="michale"></div><div class="profile-name">Michale Suyama</div><table><tr><td class="e-bold">Title</td><td>Inside Sales Coordinator</td></tr><tr><td class="e-bold">Hire Date</td><td>06/10/1998</td></tr><tr><td class="e-bold">Address</td><td>4726 - 11th Ave. N.E.</td></tr><tr><td class="e-bold">City</td><td>Seattle</td></tr><tr><td class="e-bold">Phone</td><td>(206) 555-1189</td></tr></table>';
                break;
            case '5':
                splitterInstance.current.paneSettings[1].content = '<div class="header-image"><div class="e-avatar e-avatar-circle e-avatar-xlarge"><img src="https://ej2.syncfusion.com/react/demos/src/splitter/images/albert.png" alt="albert"></div><div class="profile-name">Albert Dodsworth</div><table><tr><td class="e-bold">Title</td><td>Sales Representative</td></tr><tr><td class="e-bold">Hire Date</td><td>10/5/1996</td></tr><tr><td class="e-bold">Address</td><td>7 Houndstooth Rd.</td></tr><tr><td class="e-bold">City</td><td>London</td></tr><tr><td class="e-bold">Phone</td><td>(71) 555-4444</td></tr></table>';
                break;
        }
    };
    const onCreate = () => {
        listViewObj.current.selectItem(dataSource.data1[0]);
        splitterInstance.current.element.querySelectorAll('.e-pane')[0].setAttribute('id', 'ui-list');
        splitterInstance.current.element.querySelectorAll('.e-pane')[1].setAttribute('id', 'content');
        liElement = splitterInstance.current.element.querySelector("#ui-list");
        if (Browser.isDevice) {
            liElement.classList.add('ui-mobile');
        }
    };
    const ListViewContent = () => {
        return (<ListViewComponent className='splitter-list' height='289' dataSource={dataSource.data1} enableVirtualization={true} cssClass={'e-list-template'} template={template} select={onSelect.bind(this)} ref={listViewObj}>
            <Inject services={[Virtualization]}/> 
            </ListViewComponent>);
    };
    return (<div id="target" className="control-section details-view">
            <SplitterComponent id="detailSplitter" height="292px" width="100%" ref={splitterInstance} created={onCreate.bind(this)}>
                <PanesDirective>
                    <PaneDirective size={firstPaneSize} min={firstPaneMinSize} content={ListViewContent.bind(this)}/>
                    <PaneDirective size={secondPaneSize} min={secondPaneMinSize}/>
                </PanesDirective>
            </SplitterComponent>
        </div>);
};
export default DetailsView;

const root = createRoot(document.getElementById('sample'));
root.render(<DetailsView />);
