index.css:
@font-face {
    font-family: 'Stepper-Default';
    src:
    url(data:application/x-font-ttf;charset=utf-8;base64,) format('truetype');
    font-weight: normal;
    font-style: normal;
}

.default-stepper-section [class^="sf-icon-"], .default-stepper-section [class*=" sf-icon-"] {
    font-family: 'Stepper-Default' !important;
    speak: none;
    font-style: normal;
    font-weight: normal;
    font-variant: normal;
    text-transform: none;
    line-height: inherit;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
}

.default-stepper-section .sf-icon-cart:before { content: "\e710"; }
.default-stepper-section .sf-icon-user:before { content: "\e708"; }
.default-stepper-section .sf-icon-transport:before { content: "\e702"; }
.default-stepper-section .sf-icon-payment:before { content: "\e706"; }
.default-stepper-section .sf-icon-success:before { content: "\e715"; }

.default-stepper-section {
    width: 75%;
    margin: 0px auto;
    min-width: 85px;
    padding: 25px 0;
}

.default-stepper-section nav {
    margin: 20px 0;
}

.default-stepper-section nav#iconWithLabel {
    margin: 20px 0 0 0;
}

.default-stepper-section label {
    margin-top: 22px;
}

@media only screen and (max-width: 480px) {
    .default-stepper-section {
        width: 92%;
        margin: 0 auto;
    }
}

