index.css:
@font-face {
    font-family: 'stepper-icons';
    src:
        url(data:application/x-font-ttf;charset=utf-8;base64,) format('truetype');
    font-weight: normal;
    font-style: normal;
}

.orientation-stepper-section [class^="sf-icon-"],
.orientation-stepper-section [class*=" sf-icon-"] {
    font-family: 'stepper-icons' !important;
    speak: none;
    font-style: normal;
    font-weight: normal;
    font-variant: normal;
    text-transform: none;
    line-height: inherit;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
}

.orientation-stepper-section .sf-icon-ordered:before { content: "\e704"; }
.orientation-stepper-section .sf-icon-delivery:before { content: "\e705"; }
.orientation-stepper-section .sf-icon-review:before { content: "\e708"; }
.orientation-stepper-section .sf-icon-package:before { content: "\e709"; }

.orientation-stepper-section .orientation-header-wrapper {
    display: flex;
    flex-direction: column;
    align-items: center;
}

.orientation-header-wrapper p {
    font-family: Arial, Helvetica, sans-serif;
    font-size: 15px;
    font-weight: 600;
}

.orientation-stepper-section {
    width: 75%;
    margin: 0 auto;
}

.orientation-stepper-section .orientation-stepper-control:has(.e-vertical) {
    min-height: 450px;
    margin: 4% 40% 2%;
}

.orientation-stepper-section .orientation-stepper-control:has(.e-horizontal) {
    width: 85%;
    margin: auto;
    padding: 13% 0;
}

.orientation-stepper-section #orientation-options {
    margin: 0 auto;
    align-items: center;
    justify-content: space-evenly;
    display: flex;
    flex-wrap: wrap;
}

@media only screen and (max-width: 900px) {
    .orientation-stepper-section {
        width: 90%;
    }
    .orientation-stepper-section .orientation-stepper-control:has(.e-horizontal) {
        width: 100%;
    }
    .orientation-stepper-section #orientation-options {
        gap: 15px;
    }
}

