index.css:
@font-face {
    font-family: 'Survey-icons';
    src:
        url(data:application/x-font-ttf;charset=utf-8;base64,) format('truetype');
    font-weight: normal;
    font-style: normal;
}

.validation-stepper-section [class^="sf-icon-"],
.validation-stepper-section [class*=" sf-icon-"] {
    font-family: 'Survey-icons' !important;
    speak: none;
    font-size: 55px;
    font-style: normal;
    font-weight: normal;
    font-variant: normal;
    text-transform: none;
    line-height: inherit;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
}

.validation-stepper-section .sf-icon-survey-feedback:before {
    content: "\e700";
}

.validation-stepper-section .sf-icon-survey-intro:before {
    content: "\e703";
}

.validation-stepper-section .sf-icon-survey-status:before {
    content: "\e707";
}

#validation-step-content input[type=radio] {
    vertical-align: top;
}

.feedback-label {
    margin-bottom: 10px;
}

#feedback-text {
    color: #000;
    width: 90%;
    height: 50px;
    resize: vertical;
}

#agreement-text p {
    text-align: justify;
}


#validation-step-content .success-message {
    color: green;
    font-weight: bold;
    margin-top: 10px;
    font-size: 16px;
}

#validation-step-content .required {
    color: red;
}

#validation-step-content .content-wrapper {
    min-height: 200px;
}

#validation-step-content .error-label {
    height: 20px;
    color: #e5184b;
    font-size: 12px;
    font-weight: 600;
    line-height: 15px;
    visibility: hidden;
    margin: 3px 0 0;
}

#validation-step-content .question-container {
    display: flex;
    flex-direction: column;
    gap: 10px;
}

.validation-stepper-section {
    width: 70%;
    margin: 40px auto;
}

#validation-step-content {
    position: relative;
    width: 65%;
    min-height: 250px;
    margin: 0 auto;
}

.step-content {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    opacity: 0;
    pointer-events: none;
}

/* Display the active step content */
.step-content.step-active {
    animation: fadeInUp 0.4s;
    opacity: 1;
    pointer-events: all;
}

/* Keyframes */
@keyframes fadeInUp {
    from {
        opacity: 0;
        -webkit-transform: translate3d(0, 50%, 0);
        transform: translate3d(0, 50%, 0);
    }

    to {
        opacity: 1;
        -webkit-transform: none;
        transform: none;
    }
}

@media only screen and (max-width: 965px) {
    .validation-stepper-section {
        width: 100%;
    }

    #validation-step-content {
        width: auto;
        margin: auto 5%;
    }
}

