import { createRoot } from 'react-dom/client';
import './index.css';
/**
 * Sample for Circular Donut 3D Chart.
 */
import * as React from "react";
import { PieSeries3D, CircularChartDataLabel3D, CircularChartLegend3D, CircularChartTooltip3D, CircularChart3DComponent, CircularChart3DSeriesCollectionDirective, CircularChart3DSeriesDirective, Inject } from '@syncfusion/ej2-react-charts';
import { Browser } from '@syncfusion/ej2-base';
import { SampleBase } from './sample-base';
export let data1 = [{ x: 'Tesla', y: 137429 }, { x: 'Aion', y: 80308 }, { x: 'Wuling', y: 76418 }, { x: 'Changan', y: 52849 }, { x: 'Geely', y: 47234 }, { x: 'Nio', y: 31041 }, { x: 'Neta', y: 22449 }, { x: 'BMW', y: 18733 }];
const SAMPLE_CSS = `
    .control-fluid {
		padding: 0px !important;
    }`;
/**
 * Circular Donut 3D Chart.
 */
export class DonutSeries extends SampleBase {
    render() {
        return (<div className='control-pane'>
                <style>
                    {SAMPLE_CSS}
                </style>
                <div className='control-section'>
                    <div>
                        <CircularChart3DComponent id='charts' style={{ textAlign: "center" }} legendSettings={{ visible: false }} tilt={-30} enableRotation={true} load={this.load.bind(this)} title='Top Selling Electric Cars in China' loaded={this.onChartLoad.bind(this)} tooltip={{ enable: true, header: "${point.x}", format: 'Sales Count : <b>${point.y}' }}>
                            <Inject services={[PieSeries3D, CircularChartDataLabel3D, CircularChartLegend3D, CircularChartTooltip3D]}/>
                            <CircularChart3DSeriesCollectionDirective>
                                <CircularChart3DSeriesDirective dataSource={data1} xName='x' yName='y' innerRadius='65%' radius={Browser.isDevice ? '45%' : '75%'} dataLabel={{ visible: true, name: 'x', position: 'Outside', font: { fontWeight: '600', }, connectorStyle: { length: Browser.isDevice ? '20px' : '40px' } }}>
                                </CircularChart3DSeriesDirective>
                            </CircularChart3DSeriesCollectionDirective>
                        </CircularChart3DComponent>
                    </div>
                </div>
            </div>);
    }
    onChartLoad(args) {
        let chart = document.getElementById('charts');
        chart.setAttribute('title', '');
    }
    ;
    load(args) {
        let selectedTheme = location.hash.split('/')[1];
        selectedTheme = selectedTheme ? selectedTheme : 'Fluent2';
        args.chart.theme = (selectedTheme.charAt(0).toUpperCase() + selectedTheme.slice(1)).
            replace(/-dark/i, "Dark").replace(/contrast/i, 'Contrast').replace(/-highContrast/i, 'HighContrast');
    }
    ;
}

const root = createRoot(document.getElementById('sample'));
root.render(<DonutSeries />);

