index.css:
@font-face {
    font-family: 'timeline-icons';
    src: url(data:application/x-font-ttf;charset=utf-8;base64,) format('truetype');
    font-weight: normal;
    font-style: normal;
  }

  .timeline-template-section [class^="sf-icon-"],
  .timeline-template-section [class*=" sf-icon-"] {
    font-family: 'timeline-icons' !important;
    speak: none;
    font-style: normal;
    font-weight: normal;
    font-variant: normal;
    text-transform: none;
    line-height: 1;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
  }

  .timeline-template-section .sf-icon-pull:before {
    content: "\e701";
  }

  .timeline-template-section .sf-icon-review:before {
    content: "\e703";
  }

  .timeline-template-section .sf-icon-create:before {
    content: "\e705";
  }

  .timeline-template-section .sf-icon-commit:before {
    content: "\e706";
  }

  .timeline-template-section .color-success {
    color: #1f883d;
  }

  .timeline-template-section .color-danger {
    color: #d1242f;
  }

  .timeline-template-section .color-pr {
    color: #a371f7;
    margin: 5px 0px;
    fill: currentColor;
  }

  .timeline-template-section {
    width: 65%;
    margin: 25px auto 0;
  }

  .custom-timeline * {
    font-size: 12px;
  }

  .custom-timeline {
    font-family: Tahoma, Verdana !important;
  }

  .custom-timeline .template-container {
    width: 100%;
    display: flex;
  }

  .custom-timeline.e-vertical .template-container .timeline-content {
    flex: 1;
    padding: 0 10px;
    margin-bottom: 30px;
    cursor: default;
  }

  .custom-timeline .content-container {
    display: flex;
    justify-content: space-between;
    margin: 5px 0px 10px 0px;
  }

  .custom-timeline .content-container .repo-name {
    font-family: Tahoma, Verdana !important;
    font-size: 11px;
    color: #2f77cf;
  }

  .custom-timeline .template-container .mention-group {
    display: flex;
    flex-direction: row;
    gap: 5px;
    border: 1px solid #afb8c170;
    border-radius: 5px;
    text-align: start;
    padding: 10px;
  }

  .custom-timeline .template-container .mention-group:hover {
    background-color: #fbfbfb;
    transition: all 0.3s;
  }

  .custom-timeline .mention-content>* {
    margin: 0;
    padding: 5px;
  }

  .custom-timeline .diffstats>* {
    font-size: 10px;
  }

  .custom-timeline span.mini-text {
    font-size: 10px;
  }

  .mini-circle {
    display: inline-block;
    width: 15px;
    height: 15px;
    margin: 0px 6px;
    border-radius: 50%;
    background-color: rgb(237, 237, 72);
    vertical-align: text-bottom;
  }

  .diff-block {
    display: inline-block;
    width: 8px;
    height: 8px;
    margin-left: 1px;
  }

  .diff-block.added {
    background-color: #1f883d;
  }

  .diff-block.neutral {
    background-color: #afb8c170;
  }

  /* START --- Customizing Icon and progress line */
  .custom-timeline .template-container .indicator {
    position: relative;
    z-index: 1000;
    min-width: 25px;
    min-height: 25px;
    font-size: 14px;
    border-radius: 50%;
    background-color: #e1e9ec;
    display: flex;
    align-items: center;
    justify-content: center;
    cursor: default;
  }

  body.fluent2-highcontrast .custom-timeline .template-container .indicator {
    color: #424242;
  }

  .custom-timeline .progress-line::after {
    content: '';
    position: absolute;
    top: 0;
    left: 50%;
    width: 2px;
    height: 100%;
    background-color: #e1e9ec;
    z-index: 999;
  }

  .custom-timeline .progress-line {
    position: relative;
  }

  /* END --- Customizing Icon and progress line */

  .timeline-template-section .e-timeline-item > div {
    width: 100%;
  }

  @media screen and (max-width: 600px) {
    .timeline-template-section {
      width: 90%;
    }

    .custom-timeline .template-container .mention-group {
      padding: 5px;
    }
  }

  /* Generic dark theme modifications */
  body[class*="-dark"] .custom-timeline .template-container .indicator,
  .highcontrast .custom-timeline .template-container .indicator {
    color: #000;
  }

  body[class*="-dark"] .custom-timeline .template-container .mention-group:hover,
  .highcontrast .custom-timeline .template-container .mention-group:hover {
    background-color: #545353;
  }

