import { createRoot } from 'react-dom/client';
import './index.css';
/**
 * Tooltip default sample
 */
import * as React from 'react';
import { TooltipComponent } from '@syncfusion/ej2-react-popups';
import { ButtonComponent } from '@syncfusion/ej2-react-buttons';
import { SampleBase } from './sample-base';
import { PropertyPane } from './property-pane';
export class Default extends SampleBase {
    constructor(props) {
        super(props);
        this.state = { position: 'TopCenter' };
    }
    //Handle tooltip position based on drop-down value change
    change(args) {
        this.setState({
            position: args.currentTarget.value
        });
    }
    render() {
        return (<div className='control-pane'>
        <div className='control-section row'>
          <div className='col-lg-8' style={{ minHeight: '350px' }}>

            {/* Tooltip element */}
            <TooltipComponent content="Let's go green to save the planet!!" position={this.state.position} tabIndex={0} style={{ display: 'block', position: 'absolute', left: 'calc( 50% - 60px)', top: '45%' }}>

              {/* Button element */}
              <ButtonComponent tabIndex={-1}>Show Tooltip</ButtonComponent>
            </TooltipComponent>
          </div>
          <div className='col-lg-4 property-section'>
            <PropertyPane title='Properties'>
              <table id='property' title='Properties' className='property-panel-table' style={{ width: '100%' }}>
                <tr>
                <td style={{ width: '30%', paddingTop: '4px' }}>
                <div>Position</div>
                  </td>
                  <td style={{ width: '70%', paddingRight: '10px' }}>
                    <div>
                      <select id='ddl' name='ddl' onChange={this.change.bind(this)} className='form-control' style={{ padding: '6px' }} aria-label="positions">
                        <option value="TopLeft">Top Left</option>
                        <option value="TopCenter" selected>Top Center</option>
                        <option value="TopRight">Top Right</option>
                        <option value="BottomLeft">Bottom Left</option>
                        <option value="BottomCenter">Bottom Center</option>
                        <option value="BottomRight">Bottom Right</option>
                        <option value="LeftTop">Left Top</option>
                        <option value="LeftCenter">Left Center</option>
                        <option value="LeftBottom">Left Bottom</option>
                        <option value="RightTop">Right Top</option>
                        <option value="RightCenter">Right Center</option>
                        <option value="RightBottom">Right Bottom</option>
                      </select>
                    </div>
                  </td>
                </tr>
              </table>
            </PropertyPane>
          </div>
        </div>


      </div>);
    }
}

const root = createRoot(document.getElementById('sample'));
root.render(<Default />);

