import { createRoot } from 'react-dom/client';
import './index.css';
/**
 * Tooltip smart position sample
 */
import * as React from 'react';
import { TooltipComponent } from '@syncfusion/ej2-react-popups';
import { Draggable } from '@syncfusion/ej2-base';
import { SampleBase } from './sample-base';
const SAMPLE_CSS = `
#targetContainer {
    border: 1px solid #dddddd;
    min-height: 350px;
    position: relative;
    overflow: hidden;
}
#demoSmart {
    background-image: url('https://ej2.syncfusion.com/react/demos/src/tooltip/images/smartposition.png');
    background-size: 100px 100px;
    height: 100px;
    position: absolute;
    left: calc( 50% - 50px);
    top: calc( 50% - 50px);
    width: 100px;
}`;
export class DraggableTooltip extends SampleBase {
    tooltipInstance;
    //Set tooltip animation
    tooltipAnimation = {
        open: { effect: 'None' },
        close: { effect: 'None' }
    };
    rendereComplete() {
        //Handle tooltip smart positioning.
        let ele = document.getElementById('demoSmart');
        let drag = new Draggable(ele, {
            clone: false,
            dragArea: '#targetContainer',
            drag: (args) => {
                if (args.element.getAttribute('data-tooltip-id') === null) {
                    this.tooltipInstance.open(args.element);
                }
                else {
                    this.tooltipInstance.refresh(args.element);
                }
            },
            dragStart: (args) => {
                if (args.element.getAttribute('data-tooltip-id') === null) {
                    this.tooltipInstance.open(args.element);
                }
            },
            dragStop: (args) => {
                this.tooltipInstance.close();
            }
        });
    }
    render() {
        return (<div className='control-pane'>
                <style>
                    {SAMPLE_CSS}
                </style>
                <div className='control-section'>

                    {/* Tooltip element */}
                    <TooltipComponent id='targetContainer' ref={t => this.tooltipInstance = t} content='Drag me anywhere, to start walking with me !!!' offsetX={-15} target='#demoSmart' animation={this.tooltipAnimation}>
                        <div id='demoSmart'></div>
                    </TooltipComponent>
                </div>


            </div>);
    }
}

const root = createRoot(document.getElementById('sample'));
root.render(<DraggableTooltip />);

