import * as ReactDOM from 'react-dom';
import * as React from 'react';
import { select } from '@syncfusion/ej2-base';
export class PropertyPane extends React.Component {
    render() {
        const mobilePropPane = select('.sb-mobile-prop-pane');
        const isMobile = window.matchMedia('(max-width:550px)').matches;
        return isMobile && mobilePropPane ?
            ReactDOM.createPortal(<div className='property-panel-section'>
                    <div className="property-panel-header">
                        {this.props.title}
                    </div>
                    <div className="property-panel-content">
                        {this.props.children}
                    </div>
                </div>, mobilePropPane)
            :
                (<div className='property-panel-section'>
                    <div className="property-panel-header">
                        {this.props.title}
                    </div>
                    <div className="property-panel-content">
                        {this.props.children}
                    </div>
                </div>);
    }
}

index.js:
import { createRoot } from 'react-dom/client';
import './index.css';
import * as React from 'react';
import { useEffect, useState } from 'react';

import { TreeViewComponent } from '@syncfusion/ej2-react-navigations';

import { CheckBoxComponent } from '@syncfusion/ej2-react-buttons';
import { PropertyPane } from './property-pane';
import * as dataSource from './dataSource/checkbox-data.json';
const Checkbox = () => {
    const [isAutoCheck, setIsAutoCheck] = useState(true);
    const data = dataSource;
    const fields = { dataSource: data.checkboxData, id: 'id', parentID: 'pid', text: 'name', hasChildren: 'hasChild' };
    const onChange = (args) => {
        setIsAutoCheck(args.checked);
    };
    return (<div className='control-pane'>
            <div className='col-lg-8 control-section'>
                <div className='tree-control_wrapper'>
                    {/* Render the TreeView with checkboxes */}
                    <TreeViewComponent fields={fields} showCheckBox={true} autoCheck={isAutoCheck}/>
                </div>
            </div>
            <div className='col-lg-4 property-section'>
                <PropertyPane title='Properties'>
                    <table id="property" title="Properties">
                        <tbody>
                            <tr>
                                <td>
                                    <div>
                                        <CheckBoxComponent id="check" checked={true} label='Auto Check' change={onChange.bind(this)}></CheckBoxComponent>
                                    </div>
                                </td>
                            </tr>
                        </tbody>
                    </table>
                </PropertyPane>
            </div>
        </div>);
};
export default Checkbox;

const root = createRoot(document.getElementById('sample'));
root.render(<Checkbox />);
